migrations/relations.ts.content:
import { relations } from "drizzle-orm/relations";
import { users, activities, keyResults, checkpoints, objectives, regions, subRegions, solutions, serviceLines, services, actions, strategicIndicators } from "./schema";

export const activitiesRelations = relations(activities, ({one}) => ({
	user: one(users, {
		fields: [activities.userId],
		references: [users.id]
	}),
}));

export const usersRelations = relations(users, ({one, many}) => ({
	activities: many(activities),
	objectives: many(objectives),
	actions: many(actions),
	region: one(regions, {
		fields: [users.regionId],
		references: [regions.id]
	}),
	subRegion: one(subRegions, {
		fields: [users.subRegionId],
		references: [subRegions.id]
	}),
}));

export const checkpointsRelations = relations(checkpoints, ({one}) => ({
	keyResult: one(keyResults, {
		fields: [checkpoints.keyResultId],
		references: [keyResults.id]
	}),
}));

export const keyResultsRelations = relations(keyResults, ({one, many}) => ({
	checkpoints: many(checkpoints),
	objective: one(objectives, {
		fields: [keyResults.objectiveId],
		references: [objectives.id]
	}),
	serviceLine: one(serviceLines, {
		fields: [keyResults.serviceLineId],
		references: [serviceLines.id]
	}),
	service: one(services, {
		fields: [keyResults.serviceId],
		references: [services.id]
	}),
	actions: many(actions),
}));

export const objectivesRelations = relations(objectives, ({one, many}) => ({
	user: one(users, {
		fields: [objectives.ownerId],
		references: [users.id]
	}),
	region: one(regions, {
		fields: [objectives.regionId],
		references: [regions.id]
	}),
	subRegion: one(subRegions, {
		fields: [objectives.subRegionId],
		references: [subRegions.id]
	}),
	keyResults: many(keyResults),
}));

export const regionsRelations = relations(regions, ({many}) => ({
	objectives: many(objectives),
	subRegions: many(subRegions),
	users: many(users),
}));

export const subRegionsRelations = relations(subRegions, ({one, many}) => ({
	objectives: many(objectives),
	region: one(regions, {
		fields: [subRegions.regionId],
		references: [regions.id]
	}),
	users: many(users),
}));

export const serviceLinesRelations = relations(serviceLines, ({one, many}) => ({
	solution: one(solutions, {
		fields: [serviceLines.solutionId],
		references: [solutions.id]
	}),
	services: many(services),
	keyResults: many(keyResults),
}));

export const solutionsRelations = relations(solutions, ({many}) => ({
	serviceLines: many(serviceLines),
}));

export const servicesRelations = relations(services, ({one, many}) => ({
	serviceLine: one(serviceLines, {
		fields: [services.serviceLineId],
		references: [serviceLines.id]
	}),
	keyResults: many(keyResults),
}));

export const actionsRelations = relations(actions, ({one}) => ({
	keyResult: one(keyResults, {
		fields: [actions.keyResultId],
		references: [keyResults.id]
	}),
	strategicIndicator: one(strategicIndicators, {
		fields: [actions.strategicIndicatorId],
		references: [strategicIndicators.id]
	}),
	user: one(users, {
		fields: [actions.responsibleId],
		references: [users.id]
	}),
}));

export const strategicIndicatorsRelations = relations(strategicIndicators, ({many}) => ({
	actions: many(actions),
}));

migrations/schema.ts.content:
import { pgTable, foreignKey, serial, integer, text, jsonb, timestamp, numeric, unique, boolean } from "drizzle-orm/pg-core"
import { sql } from "drizzle-orm"



export const activities = pgTable("activities", {
	id: serial().primaryKey().notNull(),
	userId: integer("user_id").notNull(),
	entityType: text("entity_type").notNull(),
	entityId: integer("entity_id").notNull(),
	action: text().notNull(),
	description: text().notNull(),
	oldValues: jsonb("old_values"),
	newValues: jsonb("new_values"),
	createdAt: timestamp("created_at", { mode: 'string' }).defaultNow(),
}, (table) => [
	foreignKey({
			columns: [table.userId],
			foreignColumns: [users.id],
			name: "activities_user_id_users_id_fk"
		}),
]);

export const checkpoints = pgTable("checkpoints", {
	id: serial().primaryKey().notNull(),
	keyResultId: integer("key_result_id").notNull(),
	period: text().notNull(),
	targetValue: numeric("target_value", { precision: 15, scale:  2 }).notNull(),
	actualValue: numeric("actual_value", { precision: 15, scale:  2 }),
	progress: numeric({ precision: 5, scale:  2 }).default('0'),
	status: text().default('pending').notNull(),
	notes: text(),
	completedAt: timestamp("completed_at", { mode: 'string' }),
	createdAt: timestamp("created_at", { mode: 'string' }).defaultNow(),
	updatedAt: timestamp("updated_at", { mode: 'string' }).defaultNow(),
}, (table) => [
	foreignKey({
			columns: [table.keyResultId],
			foreignColumns: [keyResults.id],
			name: "checkpoints_key_result_id_key_results_id_fk"
		}).onDelete("cascade"),
]);

export const objectives = pgTable("objectives", {
	id: serial().primaryKey().notNull(),
	title: text().notNull(),
	description: text(),
	ownerId: integer("owner_id").notNull(),
	regionId: integer("region_id"),
	subRegionId: integer("sub_region_id"),
	startDate: timestamp("start_date", { mode: 'string' }).notNull(),
	endDate: timestamp("end_date", { mode: 'string' }).notNull(),
	status: text().default('active').notNull(),
	progress: numeric({ precision: 5, scale:  2 }).default('0'),
	createdAt: timestamp("created_at", { mode: 'string' }).defaultNow(),
	updatedAt: timestamp("updated_at", { mode: 'string' }).defaultNow(),
}, (table) => [
	foreignKey({
			columns: [table.ownerId],
			foreignColumns: [users.id],
			name: "objectives_owner_id_users_id_fk"
		}),
	foreignKey({
			columns: [table.regionId],
			foreignColumns: [regions.id],
			name: "objectives_region_id_regions_id_fk"
		}),
	foreignKey({
			columns: [table.subRegionId],
			foreignColumns: [subRegions.id],
			name: "objectives_sub_region_id_sub_regions_id_fk"
		}),
]);

export const serviceLines = pgTable("service_lines", {
	id: serial().primaryKey().notNull(),
	name: text().notNull(),
	description: text(),
	solutionId: integer("solution_id").notNull(),
	createdAt: timestamp("created_at", { mode: 'string' }).defaultNow(),
}, (table) => [
	foreignKey({
			columns: [table.solutionId],
			foreignColumns: [solutions.id],
			name: "service_lines_solution_id_solutions_id_fk"
		}),
]);

export const services = pgTable("services", {
	id: serial().primaryKey().notNull(),
	name: text().notNull(),
	description: text(),
	serviceLineId: integer("service_line_id").notNull(),
	createdAt: timestamp("created_at", { mode: 'string' }).defaultNow(),
}, (table) => [
	foreignKey({
			columns: [table.serviceLineId],
			foreignColumns: [serviceLines.id],
			name: "services_service_line_id_service_lines_id_fk"
		}),
]);

export const keyResults = pgTable("key_results", {
	id: serial().primaryKey().notNull(),
	objectiveId: integer("objective_id").notNull(),
	title: text().notNull(),
	description: text(),
	number: integer().notNull(),
	serviceLineId: integer("service_line_id"),
	serviceId: integer("service_id"),
	initialValue: numeric("initial_value", { precision: 15, scale:  2 }).notNull(),
	targetValue: numeric("target_value", { precision: 15, scale:  2 }).notNull(),
	currentValue: numeric("current_value", { precision: 15, scale:  2 }).default('0'),
	unit: text(),
	frequency: text().notNull(),
	startDate: timestamp("start_date", { mode: 'string' }).notNull(),
	endDate: timestamp("end_date", { mode: 'string' }).notNull(),
	progress: numeric({ precision: 5, scale:  2 }).default('0'),
	status: text().default('active').notNull(),
	createdAt: timestamp("created_at", { mode: 'string' }).defaultNow(),
	updatedAt: timestamp("updated_at", { mode: 'string' }).defaultNow(),
	strategicIndicatorIds: integer("strategic_indicator_ids").array(),
}, (table) => [
	foreignKey({
			columns: [table.objectiveId],
			foreignColumns: [objectives.id],
			name: "key_results_objective_id_objectives_id_fk"
		}).onDelete("cascade"),
	foreignKey({
			columns: [table.serviceLineId],
			foreignColumns: [serviceLines.id],
			name: "key_results_service_line_id_service_lines_id_fk"
		}),
	foreignKey({
			columns: [table.serviceId],
			foreignColumns: [services.id],
			name: "key_results_service_id_services_id_fk"
		}),
]);

export const regions = pgTable("regions", {
	id: serial().primaryKey().notNull(),
	name: text().notNull(),
	code: text().notNull(),
}, (table) => [
	unique("regions_name_unique").on(table.name),
	unique("regions_code_unique").on(table.code),
]);

export const subRegions = pgTable("sub_regions", {
	id: serial().primaryKey().notNull(),
	name: text().notNull(),
	code: text().notNull(),
	regionId: integer("region_id").notNull(),
}, (table) => [
	foreignKey({
			columns: [table.regionId],
			foreignColumns: [regions.id],
			name: "sub_regions_region_id_regions_id_fk"
		}),
	unique("sub_regions_code_unique").on(table.code),
]);

export const solutions = pgTable("solutions", {
	id: serial().primaryKey().notNull(),
	name: text().notNull(),
	description: text(),
	createdAt: timestamp("created_at", { mode: 'string' }).defaultNow(),
}, (table) => [
	unique("solutions_name_unique").on(table.name),
]);

export const actions = pgTable("actions", {
	id: serial().primaryKey().notNull(),
	keyResultId: integer("key_result_id").notNull(),
	title: text().notNull(),
	description: text(),
	number: integer().notNull(),
	strategicIndicatorId: integer("strategic_indicator_id"),
	responsibleId: integer("responsible_id"),
	dueDate: timestamp("due_date", { mode: 'string' }),
	status: text().default('pending').notNull(),
	priority: text().default('medium').notNull(),
	createdAt: timestamp("created_at", { mode: 'string' }).defaultNow(),
	updatedAt: timestamp("updated_at", { mode: 'string' }).defaultNow(),
}, (table) => [
	foreignKey({
			columns: [table.keyResultId],
			foreignColumns: [keyResults.id],
			name: "actions_key_result_id_key_results_id_fk"
		}).onDelete("cascade"),
	foreignKey({
			columns: [table.strategicIndicatorId],
			foreignColumns: [strategicIndicators.id],
			name: "actions_strategic_indicator_id_strategic_indicators_id_fk"
		}),
	foreignKey({
			columns: [table.responsibleId],
			foreignColumns: [users.id],
			name: "actions_responsible_id_users_id_fk"
		}),
]);

export const strategicIndicators = pgTable("strategic_indicators", {
	id: serial().primaryKey().notNull(),
	name: text().notNull(),
	description: text(),
	unit: text(),
	active: boolean().default(true).notNull(),
}, (table) => [
	unique("strategic_indicators_name_unique").on(table.name),
]);

export const users = pgTable("users", {
	id: serial().primaryKey().notNull(),
	username: text().notNull(),
	password: text().notNull(),
	name: text().notNull(),
	email: text().notNull(),
	role: text().default('operacional').notNull(),
	regionId: integer("region_id"),
	subRegionId: integer("sub_region_id"),
	active: boolean().default(true).notNull(),
	createdAt: timestamp("created_at", { mode: 'string' }).defaultNow(),
}, (table) => [
	foreignKey({
			columns: [table.regionId],
			foreignColumns: [regions.id],
			name: "users_region_id_regions_id_fk"
		}),
	foreignKey({
			columns: [table.subRegionId],
			foreignColumns: [subRegions.id],
			name: "users_sub_region_id_sub_regions_id_fk"
		}),
	unique("users_username_unique").on(table.username),
	unique("users_email_unique").on(table.email),
]);

server/add-manager-fields.ts.content:
import { db } from './db';
import { sql } from 'drizzle-orm';

async function addManagerFields() {
  try {
    // Add new columns for manager and approval system
    await db.run(sql`ALTER TABLE users ADD COLUMN gestor_id INTEGER REFERENCES users(id)`);
    await db.run(sql`ALTER TABLE users ADD COLUMN approved INTEGER DEFAULT 0`);
    await db.run(sql`ALTER TABLE users ADD COLUMN approved_at TEXT`);
    await db.run(sql`ALTER TABLE users ADD COLUMN approved_by INTEGER REFERENCES users(id)`);

    // Update existing users to be approved by default (except new ones)
    await db.run(sql`UPDATE users SET approved = 1 WHERE approved IS NULL OR approved = 0`);

    console.log('‚úÖ Successfully added manager and approval fields to users table');
  } catch (error: any) {
    if (error.message.includes('duplicate column name')) {
      console.log('‚úÖ Fields already exist, skipping migration');
    } else {
      console.error('Error adding manager fields:', error);
    }
  }
}

addManagerFields();

server/add-remaining-examples.ts.content:
import { storage } from './hybrid-storage';

async function addRemainingExamples() {
  console.log('üîß Completando exemplos restantes...');

  try {
    // Verificar dados existentes
    const objectives = await storage.getObjectives();
    const keyResults = await storage.getKeyResults();
    const actions = await storage.getActions();

    console.log(`üìä Status atual:`);
    console.log(`  Objetivos: ${objectives.length}`);
    console.log(`  Key Results: ${keyResults.length}`);
    console.log(`  A√ß√µes: ${actions.length}`);

    // Buscar usu√°rio admin
    const adminUser = await storage.getUserByUsername('admin');
    if (!adminUser) {
      throw new Error('Usu√°rio admin n√£o encontrado');
    }

    // Se temos menos de 10 key results, criar o √∫ltimo
    if (keyResults.length < 10) {
      const lastObjective = objectives[objectives.length - 1];
      const lastKeyResult = {
        objectiveId: lastObjective.id,
        title: 'Reduzir custos operacionais em 15%',
        description: 'Otimizar processos para reduzir custos e aumentar sustentabilidade',
        number: 2,
        strategicIndicatorIds: [1, 2],
        serviceLineId: 2,
        initialValue: 100,
        targetValue: 85,
        currentValue: 95,
        unit: '%',
        frequency: 'monthly',
        startDate: '2025-01-01',
        endDate: '2025-12-31',
        status: 'active'
      };

      const createdKR = await storage.createKeyResult(lastKeyResult);
      console.log(`üéØ Key Result final criado: ${createdKR.title}`);
      keyResults.push(createdKR);
    }

    // Se temos menos de 20 a√ß√µes, criar as restantes
    if (actions.length < 20) {
      const actionsToCreate = [];

      // Calcular quantas a√ß√µes criar para cada KR
      const actionsPerKR = 2;
      const currentActionsPerKR = Math.floor(actions.length / keyResults.length);

      for (let i = 0; i < keyResults.length; i++) {
        const kr = keyResults[i];
        const existingActionsForKR = actions.filter(a => a.keyResultId === kr.id).length;
        const actionsNeeded = actionsPerKR - existingActionsForKR;

        for (let j = 0; j < actionsNeeded; j++) {
          const actionNumber = existingActionsForKR + j + 1;
          const action = {
            keyResultId: kr.id,
            title: `A√ß√£o ${actionNumber} para ${kr.title.substring(0, 30)}...`,
            description: `Descri√ß√£o da a√ß√£o ${actionNumber} relacionada ao key result`,
            number: actionNumber,
            strategicIndicatorId: Array.isArray(kr.strategicIndicatorIds) ? kr.strategicIndicatorIds[0] : 1,
            responsibleId: adminUser.id,
            dueDate: '2025-06-30',
            status: j === 0 ? 'in_progress' : 'pending',
            priority: j === 0 ? 'high' : 'medium'
          };
          actionsToCreate.push(action);
        }
      }

      // Criar as a√ß√µes restantes
      let actionsCreated = 0;
      for (const action of actionsToCreate) {
        if (actions.length + actionsCreated >= 20) break;

        try {
          const created = await storage.createAction(action);
          console.log(`‚ö° A√ß√£o criada: ${created.title}`);
          actionsCreated++;
        } catch (error) {
          console.log(`‚ö†Ô∏è Erro ao criar a√ß√£o, pulando: ${error.message}`);
        }
      }

      console.log(`‚úÖ ${actionsCreated} a√ß√µes adicionais criadas`);
    }

    // Gerar checkpoints se necess√°rio
    console.log('üìÖ Verificando checkpoints...');
    for (let i = 0; i < Math.min(5, keyResults.length); i++) {
      try {
        const checkpoints = await storage.getCheckpoints(keyResults[i].id);
        if (checkpoints.length === 0) {
          await storage.generateCheckpoints(keyResults[i].id);
          console.log(`üìä Checkpoints gerados para: ${keyResults[i].title}`);
        }
      } catch (error) {
        console.log(`‚ö†Ô∏è Erro ao gerar checkpoints para KR ${i + 1}: ${error.message}`);
      }
    }

    // Status final
    const finalObjectives = await storage.getObjectives();
    const finalKeyResults = await storage.getKeyResults();
    const finalActions = await storage.getActions();

    console.log('\nüéâ Status final dos exemplos:');
    console.log(`‚úÖ ${finalObjectives.length} Objetivos`);
    console.log(`‚úÖ ${finalKeyResults.length} Key Results`);
    console.log(`‚úÖ ${finalActions.length} A√ß√µes`);

    return {
      objectives: finalObjectives.length,
      keyResults: finalKeyResults.length,
      actions: finalActions.length
    };

  } catch (error) {
    console.error('‚ùå Erro ao completar exemplos:', error);
    throw error;
  }
}

if (import.meta.url === `file://${process.argv[1]}`) {
  addRemainingExamples()
    .then(() => process.exit(0))
    .catch((error) => {
      console.error(error);
      process.exit(1);
    });
}

export { addRemainingExamples };

server/add-solution-fields.ts.content:
import Database from "better-sqlite3";

const db = new Database("./okr.db");

console.log("Adding solution, service line, and service fields to users table...");

try {
  // Add the new columns to users table
  db.exec(`
    ALTER TABLE users ADD COLUMN solution_ids TEXT DEFAULT '[]';
    ALTER TABLE users ADD COLUMN service_line_ids TEXT DEFAULT '[]';
    ALTER TABLE users ADD COLUMN service_ids TEXT DEFAULT '[]';
  `);

  console.log("‚úÖ Successfully added solution fields to users table");

  // Update existing users to have empty arrays
  db.prepare(`
    UPDATE users 
    SET solution_ids = '[]', service_line_ids = '[]', service_ids = '[]' 
    WHERE solution_ids IS NULL OR service_line_ids IS NULL OR service_ids IS NULL
  `).run();

  console.log("‚úÖ Updated existing users with empty arrays");

} catch (error: any) {
  if (error.message.includes("duplicate column name")) {
    console.log("‚ö†Ô∏è Columns already exist, skipping...");
  } else {
    console.error("‚ùå Error adding fields:", error.message);
  }
} finally {
  db.close();
}

server/auth.ts.content:
import passport from "passport";
import { Strategy as LocalStrategy } from "passport-local";
import { Express } from "express";
import session from "express-session";
import { scrypt, randomBytes, timingSafeEqual } from "crypto";
import { promisify } from "util";
import { storage } from "./storage";
import { User as SelectUser } from "@shared/schema";

declare global {
  namespace Express {
    interface User extends SelectUser {}
  }
}

const scryptAsync = promisify(scrypt);

export async function hashPassword(password: string) {
  const salt = randomBytes(16).toString("hex");
  const buf = (await scryptAsync(password, salt, 64)) as Buffer;
  return `${buf.toString("hex")}.${salt}`;
}

async function comparePasswords(supplied: string, stored: string) {
  try {
    // Handle old format (just hex hash with hardcoded salt) for backward compatibility
    if (!stored.includes(".")) {
      const suppliedBuf = (await scryptAsync(supplied, 'salt', 32)) as Buffer; // Use 32 bytes to match stored hash
      const storedBuf = Buffer.from(stored, "hex");

      // Ensure buffers are the same length for timingSafeEqual
      if (suppliedBuf.length !== storedBuf.length) {
        return false;
      }

      return timingSafeEqual(suppliedBuf, storedBuf);
    }

    // Handle new format (hash.salt)
    const [hashed, salt] = stored.split(".");
    const hashedBuf = Buffer.from(hashed, "hex");
    const suppliedBuf = (await scryptAsync(supplied, salt, 64)) as Buffer;

    // Ensure buffers are the same length for timingSafeEqual
    if (suppliedBuf.length !== hashedBuf.length) {
      return false;
    }

    return timingSafeEqual(hashedBuf, suppliedBuf);
  } catch (error) {
    console.error('Password comparison error:', error);
    return false;
  }
}

export function setupAuth(app: Express) {
  const sessionSecret = process.env.SESSION_SECRET || "default-session-secret-change-in-production";

  const sessionSettings: session.SessionOptions = {
    secret: sessionSecret,
    resave: false,
    saveUninitialized: false,
    store: storage.sessionStore,
  };

  app.set("trust proxy", 1);
  app.use(session(sessionSettings));
  app.use(passport.initialize());
  app.use(passport.session());

  passport.use(
    new LocalStrategy(async (username, password, done) => {
      try {
        const user = await storage.getUserByUsername(username);

        if (!user) {
          return done(null, false);
        }

        const passwordMatch = await comparePasswords(password, user.password);

        if (!passwordMatch) {
          return done(null, false);
        }

        // Verificar se o usu√°rio est√° aprovado (exceto admins)
        if (!user.approved && user.role !== 'admin') {
          return done(null, false, { message: "Usu√°rio aguarda aprova√ß√£o do gestor" });
        }

        return done(null, user);
      } catch (error) {
        console.error('Login error:', error);
        return done(error);
      }
    }),
  );

  passport.serializeUser((user, done) => done(null, user.id));
  passport.deserializeUser(async (id: number, done) => {
    try {
      const user = await storage.getUser(id);
      done(null, user);
    } catch (error) {
      console.error("Error deserializing user:", error);
      done(error, null);
    }
  });

  app.post("/api/register", async (req, res, next) => {
    try {
      const existingUser = await storage.getUserByUsername(req.body.username);
      if (existingUser) {
        return res.status(400).json({ message: "Nome de usu√°rio j√° existe" });
      }

      // Validar se gestorId foi fornecido
      if (!req.body.gestorId) {
        return res.status(400).json({ message: "Gestor deve ser selecionado para o registro." });
      }

      // Parse gestorId to number and validate
      const gestorId = parseInt(req.body.gestorId);
      if (isNaN(gestorId)) {
        return res.status(400).json({ message: "ID do gestor inv√°lido." });
      }

      // Verificar se o gestor existe e √© v√°lido
      const gestor = await storage.getUserById(gestorId);
      if (!gestor || (gestor.role !== 'gestor' && gestor.role !== 'admin')) {
        return res.status(400).json({ message: "Gestor selecionado inv√°lido." });
      }

      // Public registration sempre cria usu√°rios como operacional, n√£o aprovados, vinculados ao gestor
      const userToCreate = {
        ...req.body,
        role: 'operacional', // For√ßar role operacional
        approved: false,     // Aguardar aprova√ß√£o
        gestorId: gestorId, // Vincular ao gestor selecionado (n√∫mero)
        password: await hashPassword(req.body.password)
      };

      const user = await storage.createUser(userToCreate);

      // N√ÉO fazer login automaticamente - usu√°rio deve aguardar aprova√ß√£o
      res.status(201).json({ 
        message: `Usu√°rio registrado com sucesso! Aguarde aprova√ß√£o do gestor ${gestor.name}.`,
        userId: user.id 
      });
    } catch (error) {
      console.error("Error registering user:", error);
      res.status(500).json({ message: "Erro ao registrar usu√°rio" });
    }
  });

  app.post("/api/login", passport.authenticate("local"), (req, res) => {
    res.status(200).json(req.user);
  });

  app.post("/api/logout", (req, res, next) => {
    req.logout((err) => {
      if (err) return next(err);
      res.sendStatus(200);
    });
  });

  app.get("/api/user", (req, res) => {
    if (!req.isAuthenticated()) return res.sendStatus(401);
    res.json(req.user);
  });
}

