    } catch (error) {
      console.error('Error getting solutions:', error);
      throw error;
    }
  }

  async getServiceLines(solutionId?: number): Promise<ServiceLine[]> {
    try {
      // Try Microsoft Fabric first
      if (fabricConnected) {
