  async getServices(serviceLineId?: number): Promise<Service[]> {
    try {
      // Try Microsoft Fabric first
      if (fabricConnected) {
        try {
          const query = serviceLineId 
            ? 'SELECT * FROM dbo.services WHERE service_line_id = ? ORDER BY name'
