  async getKeyResult(id: number): Promise<KeyResult | undefined> {
    if (this.fabricConnected) {
      try {
        const result = await fabricQueries.executeQuery('SELECT * FROM key_results WHERE id = @param0', [id]);
        return result.recordset[0];
      } catch (error) {
