  // Key Results methods with hierarchical access control
  async getKeyResults(objectiveId?: number, currentUserId?: number): Promise<(KeyResult & { 
    objective: Objective; 
    strategicIndicator?: StrategicIndicator;
    nextCheckpoint?: Checkpoint;
  })[]> {
    let query = db.select()
    .from(keyResults)
    .leftJoin(objectives, eq(keyResults.objectiveId, objectives.id));

    const conditions = [];

    if (objectiveId) {
      conditions.push(eq(keyResults.objectiveId, objectiveId));
    }

    // Apply hierarchical access control
    if (currentUserId) {
      const user = await this.getUserById(currentUserId);
      if (user && user.role !== 'admin') {
        const userRegionIds = Array.isArray(user.regionIds) ? user.regionIds : [];
        const userSubRegionIds = Array.isArray(user.subRegionIds) ? user.subRegionIds : [];

        // Filter by user's accessible regions/sub-regions through objectives
        if (userSubRegionIds.length > 0) {
          conditions.push(inArray(objectives.subRegionId, userSubRegionIds));
        } else if (userRegionIds.length > 0) {
          conditions.push(inArray(objectives.regionId, userRegionIds));
        }
      }
    }

    if (conditions.length > 0) {
      query = query.where(and(...conditions));
    }

    const results = await query.orderBy(desc(keyResults.createdAt));

    // Debug: Check if progress field is in the raw query results
    const testResult = results.find(r => r.key_results?.title === 'Key Result Teste');
    if (testResult) {
      console.log('üîç Raw SQL result fields:', Object.keys(testResult.key_results || {}));
      console.log('üîç Progress in SQL result:', testResult.key_results?.progress);
    }

    return results.map(row => {
      const kr = row.key_results;

      // Calculate progress based on current and target values
      let calculatedProgress = 0;
      if (kr.currentValue && kr.targetValue) {
        const current = parseFloat(kr.currentValue.toString());
        const target = parseFloat(kr.targetValue.toString());
        if (target > 0) {
          calculatedProgress = Math.round((current / target) * 100 * 100) / 100; // Round to 2 decimal places
        }
      }

      // Use calculated progress if database progress is null/undefined, otherwise use database value
      const finalProgress = (kr.progress !== null && kr.progress !== undefined) 
        ? parseFloat(kr.progress.toString()) 
        : calculatedProgress;

      const mappedRow = {
        id: kr.id,
        objectiveId: kr.objectiveId,
        title: kr.title,
        description: kr.description,
        strategicIndicatorIds: kr.strategicIndicatorIds,
        serviceLineIds: kr.serviceLineIds,
        serviceId: kr.serviceId,
        initialValue: kr.initialValue,
        targetValue: kr.targetValue,
        currentValue: kr.currentValue,
        unit: kr.unit,
        frequency: kr.frequency,
        startDate: kr.startDate,
        endDate: kr.endDate,
        status: kr.status,
        progress: finalProgress, // Use calculated or database progress
        number: kr.number,
        createdAt: kr.createdAt,
        updatedAt: kr.updatedAt,
        objective: row.objectives!,
        strategicIndicator: undefined, // We'll handle strategic indicators separately
      };

      // Debug logging for Key Result Teste
      if (kr.title === 'Key Result Teste') {
        console.log('üîç Progress calculation for Key Result Teste:', {
          allFields: Object.keys(kr),
          dbProgress: kr.progress,
          dbProgressType: typeof kr.progress,
          currentValue: kr.currentValue,
          targetValue: kr.targetValue,
          calculatedProgress,
          finalProgress,
          currentParsed: parseFloat(kr.currentValue?.toString() || '0'),
          targetParsed: parseFloat(kr.targetValue?.toString() || '0')
        });
      }

      return mappedRow;
    });
  }

