  async createKeyResult(keyResult: InsertKeyResult): Promise<KeyResult> {
    if (!this.connected) throw new Error('Database not connected');

    try {
      console.log('Creating key result with data:', keyResult);

      // Garantir que valores decimais sejam v√°lidos 
      const targetValue = convertBRToDatabase(keyResult.targetValue || "0");
      const currentValue = convertBRToDatabase(keyResult.initialValue || "0");

      console.log('Processing Key Result values:', {
        originalTarget: keyResult.targetValue,
        originalInitial: keyResult.initialValue,
        processedTarget: targetValue,
        processedInitial: currentValue
      });

      console.log('About to execute INSERT with values:', [
        keyResult.objectiveId, 
        keyResult.title, 
        keyResult.description, 
        isNaN(targetValue) ? 0 : targetValue,
        isNaN(currentValue) ? 0 : currentValue, 
        keyResult.unit || null, 
        JSON.stringify(keyResult.strategicIndicatorIds || []), 
        JSON.stringify(keyResult.serviceLineIds || []), 
        keyResult.serviceId || null, 
        keyResult.startDate, 
        keyResult.endDate, 
        keyResult.frequency, 
        keyResult.status || 'active', 
        keyResult.progress || '0'
      ]);

      const insertResult = await pool.execute(
        'INSERT INTO key_results (objective_id, title, description, target_value, current_value, unit, strategicIndicatorIds, serviceLineIds, service_id, start_date, end_date, frequency, status, progress) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)',
        [
          keyResult.objectiveId, 
          keyResult.title, 
          keyResult.description, 
          isNaN(targetValue) ? 0 : targetValue,
          isNaN(currentValue) ? 0 : currentValue, 
          keyResult.unit || null, 
          JSON.stringify(keyResult.strategicIndicatorIds || []), 
          JSON.stringify(keyResult.serviceLineIds || []), 
          keyResult.serviceId || null, 
          keyResult.startDate, 
          keyResult.endDate, 
          keyResult.frequency, 
          keyResult.status || 'active', 
          keyResult.progress || '0'
        ]
      );

      // MySQL2 com pool.execute retorna [ResultSetHeader, FieldPacket[]]
      const resultSetHeader = insertResult[0] as any;
      console.log('INSERT ResultSetHeader:', {
        fieldCount: resultSetHeader.fieldCount,
        affectedRows: resultSetHeader.affectedRows, 
        insertId: resultSetHeader.insertId,
        info: resultSetHeader.info
      });

      // Extrair insertId do ResultSetHeader
      if (!resultSetHeader || typeof resultSetHeader.insertId === 'undefined') {
        console.error('No insertId in ResultSetHeader');
        throw new Error('Failed to get insert ID from MySQL database');
      }

      const insertId = Number(resultSetHeader.insertId);
      console.log('Final insertId:', insertId, 'Type:', typeof insertId);

      if (isNaN(insertId) || insertId <= 0) {
        console.error('Invalid insertId:', insertId);
        throw new Error(`Invalid insert ID: ${insertId}`);
      }

      // Usar o insertId v√°lido para buscar o registro criado
      console.log('About to SELECT with insertId:', insertId);
      const [rows] = await pool.execute(
        'SELECT * FROM key_results WHERE id = ?', 
        [insertId]
      );

      console.log('SELECT result rows:', rows);
      const newKeyResult = (rows as any[])[0];
      if (!newKeyResult) {
        console.error('Failed to retrieve created key result with ID:', insertId);
        throw new Error('Failed to create key result - record not found after insert');
      }
      console.log('Successfully created and retrieved key result:', newKeyResult);
      return newKeyResult;
    } catch (error) {
      console.error('Error creating key result:', error);
      throw error;
    }
  }

  async updateKeyResult(id: number, keyResult: Partial<InsertKeyResult>): Promise<KeyResult> {
    if (!this.connected) throw new Error('Database not connected');

    const fields = [];
    const values = [];

    // Mapeamento correto dos campos para os nomes da tabela MySQL
    const fieldMappings: Record<string, string> = {
      'objectiveId': 'objective_id',
      'targetValue': 'target_value',
      'currentValue': 'current_value',
      'strategicIndicatorIds': 'strategicIndicatorIds', // Este j√° est√° correto na tabela
      'serviceLineIds': 'serviceLineIds', // Este j√° est√° correto na tabela
      'serviceId': 'service_id',
      'startDate': 'start_date',
      'endDate': 'end_date'
    };

    for (const [key, value] of Object.entries(keyResult)) {
      if (value !== undefined) {
        const dbField = fieldMappings[key] || key; // Usa mapeamento ou o nome original

        if (key === 'strategicIndicatorIds' || key === 'serviceLineIds') {
          fields.push(`${dbField} = ?`);
          values.push(JSON.stringify(value));
        } else {
          fields.push(`${dbField} = ?`);
          values.push(value);
        }
      }
    }

    if (fields.length === 0) {
      throw new Error('No fields to update');
    }

    values.push(id);
    console.log(`Updating key result ${id} with fields:`, fields);
    console.log('Update values:', values);

    await pool.execute(`UPDATE key_results SET ${fields.join(', ')} WHERE id = ?`, values);

    const updated = await this.getKeyResult(id);
    if (!updated) throw new Error('Failed to update key result');
    return updated;
  }

