            const targetValue = convertBRToDatabase(currentKR.targetValue || '0');
            const currentValueNum = convertBRToDatabase(latestValue || '0');
            const newProgress = targetValue > 0 ? (currentValueNum / targetValue) * 100 : 0;

            console.log(`üìä Calculating progress: ${currentValueNum}/${targetValue} = ${newProgress}%`);

            await storage.updateKeyResult(existingCheckpoint.keyResultId, {
              currentValue: latestValue.toString(),
              progress: newProgress.toString()
            });

            console.log(`‚úÖ Successfully updated Key Result ${existingCheckpoint.keyResultId} currentValue to ${latestValue} and progress to ${newProgress}%`);
          } else {
            console.log(`‚ö†Ô∏è No completed checkpoints with values found for KR ${existingCheckpoint.keyResultId}`);
          }
        } catch (updateError) {
          console.error('‚ùå Error updating Key Result currentValue:', updateError);
          // Don't fail the checkpoint update if KR update fails
        }
      }

