      await storage.deleteServiceLine(id);
      res.json({ message: "Linha de servi√ßo exclu√≠da com sucesso" });
    } catch (error) {
      console.error("Error deleting service line:", error);
      res.status(500).json({ message: "Erro ao excluir linha de servi√ßo" });
    }
