import { connectToFabric, executeQuery } from './fabric-storage';
import { readFileSync } from 'fs';
import { join } from 'path';
import Database from "better-sqlite3";

// Initialize SQLite for data migration
const db = new Database("okr.db");
db.pragma("journal_mode = WAL");

async function setupFabricDatabase() {
  console.log('üöÄ Setting up Microsoft Fabric SQL Server database...');

  try {
    // Test connection
    const isConnected = await connectToFabric();
    if (!isConnected) {
      throw new Error('Failed to connect to Microsoft Fabric SQL Server');
    }

    console.log('‚úÖ Connected to Microsoft Fabric SQL Server');

    // Create schema
    await createFabricSchema();

    // Seed reference data
    await seedReferenceData();

    // Migrate existing data
    await migrateSQLiteData();

    console.log('üéâ Microsoft Fabric database setup completed successfully!');

  } catch (error) {
    console.error('‚ùå Failed to setup Microsoft Fabric database:', error.message);
    throw error;
  }
}

async function createFabricSchema() {
  console.log('üìã Creating Microsoft Fabric schema...');

  const schemaPath = join(process.cwd(), 'server', 'fabric-schema.sql');
  const schema = readFileSync(schemaPath, 'utf-8');

  // Split schema into individual statements
  const statements = schema.split(/;\s*(?=CREATE|DROP|INSERT|IF)/i)
    .filter(stmt => stmt.trim().length > 0)
    .map(stmt => stmt.trim());

  for (const statement of statements) {
    if (statement.length > 0) {
      try {
        await executeQuery(statement);
        console.log(`‚úÖ Schema: ${statement.substring(0, 50)}...`);
      } catch (error) {
        if (!error.message.includes('already exists')) {
          console.error(`‚ùå Schema error: ${error.message}`);
          console.error(`Statement: ${statement}`);
        }
      }
    }
  }
}

