    const testResult = await pool.request().query('SELECT 1 as test, GETDATE() as timestamp');
    console.log('‚úÖ Query de teste:', testResult.recordset[0]);

    // Get database info
    const dbInfo = await pool.request().query(`
      SELECT 
        DB_NAME() as database_name,
        USER_NAME() as user_name,
        SYSTEM_USER as system_user,
        SERVERPROPERTY('ProductVersion') as version
    `);
    console.log('‚úÖ Informa√ß√µes do banco:', dbInfo.recordset[0]);

    // List existing tables
    const tablesResult = await pool.request().query(`
      SELECT 
        TABLE_NAME,
        TABLE_SCHEMA,
        TABLE_TYPE
      FROM INFORMATION_SCHEMA.TABLES
      WHERE TABLE_TYPE = 'BASE TABLE'
      ORDER BY TABLE_NAME
    `);
    console.log('‚úÖ Tabelas existentes:', tablesResult.recordset.length);

    if (tablesResult.recordset.length > 0) {
      console.log('üìã Tabelas encontradas:');
      tablesResult.recordset.forEach((table, index) => {
        console.log(`   ${index + 1}. ${table.TABLE_SCHEMA}.${table.TABLE_NAME}`);
      });
    } else {
      console.log('üìã Nenhuma tabela encontrada - pronto para cria√ß√£o do schema OKR');
    }

    await pool.close();

    console.log('\nüéâ Microsoft Fabric SQL Server est√° totalmente operacional!');
    console.log('‚úÖ Autentica√ß√£o Azure AD funcionando');
    console.log('‚úÖ Conex√£o est√°vel estabelecida');
    console.log('‚úÖ Pronto para opera√ß√µes OKR');

    return {
      success: true,
      config: azureConfig,
      tablesCount: tablesResult.recordset.length
    };

  } catch (error) {
    console.error('‚ùå Falha na conex√£o Microsoft Fabric:', error.message);

    if (error.code) {
      console.log('   C√≥digo de erro:', error.code);
    }

    console.log('\nüí° Solu√ß√µes para problemas de autentica√ß√£o Azure AD:');
    console.log('1. Verificar se SQL_USERNAME est√° correto (@fiergs.org.br)');
    console.log('2. Verificar se SQL_PASSWORD est√° correto');
    console.log('3. Verificar se AZURE_CLIENT_ID est√° configurado');
    console.log('4. Confirmar que o usu√°rio tem permiss√µes no Microsoft Fabric');
    console.log('5. Verificar se o tenant Azure AD est√° correto');

    return { success: false, error: error.message };
  }
}

// Test the connection and setup
setupFabricConnection()
  .then(result => {
    if (result.success) {
      console.log('\nüöÄ Microsoft Fabric pronto para uso!');
      console.log('‚úÖ Sistema pode ser migrado para SQL Fabric');
