    } else {
      console.log('\n‚ö†Ô∏è Microsoft Fabric n√£o acess√≠vel');
      console.log('üîÑ Sistema continuar√° usando SQLite como banco prim√°rio');
      process.exit(1);
    }
  })
  .catch(error => {
    console.error('üí• Erro na configura√ß√£o:', error);
    process.exit(1);
  });

