    const quarters = await storage.getAvailableQuarters();
    console.log(`Available quarters: ${quarters.length}`);
    quarters.forEach(q => console.log(`  - ${q.id}: ${q.name}`));

    console.log('\n=== ANALYSIS ===');
    console.log(`‚úì User has access to ${objectives.length} objectives`);
    console.log(`‚úì Total key results: ${totalKRs}`);
    console.log(`‚úì Total actions: ${allActions.length}`);
