export const keyResultFormSchema = insertKeyResultSchema.omit({ 
  id: true, 
  createdAt: true, 
  updatedAt: true,
  currentValue: true,
  progress: true 
});

export const actionFormSchema = insertActionSchema.omit({ 
  id: true, 
  createdAt: true, 
  updatedAt: true,
