import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Progress } from "@/components/ui/progress";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { CalendarDays, Target, TrendingUp, RefreshCw, AlertCircle, Grid3X3, List } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import CheckpointProgressGrid from "./checkpoint-progress-grid";
import { NumberInputBR } from "@/components/ui/number-input-br";
import { parseDecimalBR, formatBrazilianNumber } from "@/lib/formatters";
import { getProgressBadgeVariant, getProgressBadgeText } from "@/lib/checkpoint-utils";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

interface CheckpointUpdaterProps {
  keyResultId?: number;
}

function CheckpointEditFormInline({ checkpoint, onClose, onUpdate }: {
  checkpoint: any;
  onClose: () => void;
  onUpdate: () => void;
}) {
  const { toast } = useToast();
  const [actualValue, setActualValue] = useState(formatBrazilianNumber(checkpoint.actualValue));
  const [notes, setNotes] = useState(checkpoint.notes || "");
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      await apiRequest("PUT", `/api/checkpoints/${checkpoint.id}`, {
        actualValue: parseDecimalBR(actualValue),
        notes,
      });

      toast({
        title: "Checkpoint atualizado",
        description: "O checkpoint foi atualizado com sucesso.",
      });

      onUpdate();
    } catch (error) {
      toast({
        title: "Erro",
        description: "Erro ao atualizar checkpoint.",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="space-y-2">
        <Label htmlFor="actualValue">Valor Atual</Label>
        <NumberInputBR
          id="actualValue"
          value={actualValue}
          onChange={setActualValue}
          placeholder="0,00"
        />
        <p className="text-sm text-muted-foreground">
          Meta: {formatBrazilianNumber(checkpoint.targetValue)}
        </p>
      </div>

      <div className="space-y-2">
        <Label htmlFor="notes">Observa√ß√µes</Label>
        <Input
          id="notes"
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          placeholder="Adicione observa√ß√µes (opcional)"
        />
      </div>

      <div className="flex justify-end gap-2">
        <Button type="button" variant="outline" onClick={onClose}>
          Cancelar
        </Button>
        <Button type="submit" disabled={isSubmitting}>
          {isSubmitting ? "Salvando..." : "Salvar"}
        </Button>
      </div>
    </form>
  );
}

export default function CheckpointUpdaterEnhanced({ keyResultId }: CheckpointUpdaterProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [selectedCheckpoint, setSelectedCheckpoint] = useState<any>(null);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);
  const [recreateProgress, setRecreateProgress] = useState(0);
  const [isRecreating, setIsRecreating] = useState(false);

  const { data: checkpoints, isLoading } = useQuery({
    queryKey: ["/api/checkpoints", keyResultId],
    queryFn: async () => {
      const url = keyResultId ? `/api/checkpoints?keyResultId=${keyResultId}` : "/api/checkpoints";
      const response = await fetch(url);
      if (!response.ok) throw new Error("Erro ao carregar checkpoints");
      return response.json();
    },
    enabled: !!keyResultId,
  });

  const handleRecreateWithAnimation = async () => {
    if (!keyResultId) return;

    setIsRecreating(true);
    setRecreateProgress(0);

    // Simular progresso da anima√ß√£o
    const progressInterval = setInterval(() => {
      setRecreateProgress(prev => {
        if (prev >= 90) return prev;
        return prev + Math.random() * 15;
      });
    }, 200);

    try {
      await apiRequest("POST", `/api/key-results/${keyResultId}/recreate-checkpoints`, {});

      // Completar progresso
      setRecreateProgress(100);

      setTimeout(() => {
        toast({
          title: "Checkpoints recriados",
          description: "Os checkpoints foram recriados com sucesso.",
        });
        queryClient.invalidateQueries({ queryKey: ["/api/checkpoints"] });
        setIsRecreating(false);
        setRecreateProgress(0);
      }, 800);

    } catch (error) {
      toast({
        title: "Erro",
        description: "Erro ao recriar checkpoints.",
        variant: "destructive",
      });
      setIsRecreating(false);
      setRecreateProgress(0);
    } finally {
      clearInterval(progressInterval);
    }
  };

  const recreateMutation = useMutation({
    mutationFn: async () => {
      if (!keyResultId) throw new Error("Key Result ID √© necess√°rio");
      await apiRequest("POST", `/api/key-results/${keyResultId}/recreate-checkpoints`, {});
    },
    onSuccess: () => {
      toast({
        title: "Checkpoints recriados",
        description: "Os checkpoints foram recriados com base na frequ√™ncia do resultado-chave.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/checkpoints"] });
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "Erro ao recriar checkpoints.",
        variant: "destructive",
      });
    },
  });

  if (isLoading) {
    return <div className="flex items-center gap-2">
      <RefreshCw className="h-4 w-4 animate-spin" />
      Carregando checkpoints...
    </div>;
  }

  if (!checkpoints || checkpoints.length === 0) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <AlertCircle className="h-5 w-5" />
            Nenhum checkpoint encontrado
          </CardTitle>
          <CardDescription>
            {keyResultId 
              ? "Este resultado-chave ainda n√£o possui checkpoints configurados."
              : "Nenhum checkpoint encontrado para os resultados-chave acess√≠veis."
            }
          </CardDescription>
        </CardHeader>
        {keyResultId && (
          <CardContent>
            <Button 
              onClick={() => setShowConfirmDialog(true)}
              disabled={isRecreating}
              className="w-full"
            >
              <RefreshCw className={`h-4 w-4 mr-2 ${isRecreating ? 'animate-spin' : ''}`} />
              {isRecreating ? "Criando..." : "Gerar Checkpoints"}
            </Button>
          </CardContent>
        )}
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div className="flex gap-2">
          <Button
            variant={viewMode === "grid" ? "default" : "outline"}
            size="sm"
            onClick={() => setViewMode("grid")}
          >
            <Grid3X3 className="h-4 w-4" />
          </Button>
          <Button
            variant={viewMode === "list" ? "default" : "outline"}
            size="sm"
            onClick={() => setViewMode("list")}
          >
            <List className="h-4 w-4" />
          </Button>
        </div>

        {keyResultId && (
          <Button 
            onClick={() => setShowConfirmDialog(true)}
            disabled={isRecreating}
            variant="outline"
            size="sm"
          >
            <RefreshCw className={`h-4 w-4 mr-2 ${isRecreating ? 'animate-spin' : ''}`} />
            {isRecreating ? "Recriando..." : "Recriar"}
          </Button>
        )}
      </div>

      {viewMode === "grid" && keyResultId && (
        <CheckpointProgressGrid 
          checkpoints={checkpoints} 
          onCheckpointClick={(checkpoint) => {
            console.log('Checkpoint clicked, opening dialog:', checkpoint);
            setSelectedCheckpoint(checkpoint);
            setIsEditDialogOpen(true);
          }}
        />
      )}

      {viewMode === "list" && (
        <div className="space-y-4">
          {checkpoints.map((checkpoint: any) => {
            const targetValue = parseFloat(checkpoint.targetValue);
            const actualValue = parseFloat(checkpoint.actualValue);

            // Verificar se √© checkpoint futuro
            const today = new Date();
            const checkpointDate = new Date(checkpoint.dueDate);
            const isFuture = checkpointDate > today;

            // Se √© futuro e n√£o tem valor registrado, n√£o mostrar 0%
            const progress = isFuture && actualValue === 0 
              ? -1  // Valor especial para indicar "aguardando"
              : targetValue > 0 ? (actualValue / targetValue) * 100 : 0;
            const badgeVariant = getProgressBadgeVariant(progress, checkpoint.dueDate);
            const badgeText = getProgressBadgeText(progress, checkpoint.dueDate);

            return (
              <Card key={checkpoint.id} className="p-4">
                <div className="flex items-center justify-between">
                  <div>
                    <h4 className="font-semibold">{checkpoint.period}</h4>
                    <p className="text-sm text-muted-foreground">
                      Meta: {checkpoint.targetValue} | Atual: {checkpoint.actualValue || '0'}
                    </p>
                    <p className="text-xs text-muted-foreground">
                      Progresso: {progress === -1 ? "Aguardando per√≠odo" : `${progress.toFixed(1)}%`}
                    </p>
                  </div>
                  <div className="text-right space-y-1">
                    <Badge variant={badgeVariant}>
                      {badgeText}
                    </Badge>
                    <div className="text-xs text-muted-foreground">
                      {checkpoint.status === 'completed' ? 'Conclu√≠do' : 
                       checkpoint.status === 'active' ? 'Ativo' : 'Pendente'}
                    </div>
                  </div>
                </div>
              </Card>
            );
          })}
        </div>
      )}

      {/* Edit Dialog */}
      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Atualizar Checkpoint</DialogTitle>
            <DialogDescription>
              {selectedCheckpoint && `Per√≠odo: ${selectedCheckpoint.period}`}
            </DialogDescription>
          </DialogHeader>
          {selectedCheckpoint && (
            <CheckpointEditFormInline 
              checkpoint={selectedCheckpoint}
              onClose={() => setIsEditDialogOpen(false)}
              onUpdate={() => {
                queryClient.invalidateQueries({ queryKey: ["/api/checkpoints"] });
                queryClient.invalidateQueries({ queryKey: ["/api/key-results"] });
                setIsEditDialogOpen(false);
              }}
            />
          )}
        </DialogContent>
      </Dialog>

      {/* Confirmation Dialog */}
      <AlertDialog open={showConfirmDialog} onOpenChange={setShowConfirmDialog}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle className="flex items-center gap-2">
              <AlertCircle className="h-5 w-5 text-orange-500" />
              Confirmar Recria√ß√£o de Checkpoints
            </AlertDialogTitle>
            <AlertDialogDescription className="space-y-2">
              <div>
                <div className="mb-2">Esta a√ß√£o ir√°:</div>
                <ul className="list-disc list-inside space-y-1 ml-4">
                  <li>Excluir todos os checkpoints existentes</li>
                  <li>Recriar novos checkpoints com base na frequ√™ncia do resultado-chave</li>
                  <li>Resetar todo o progresso atual</li>
                </ul>
                <div className="font-semibold text-orange-600 mt-3">
                  ‚ö†Ô∏è Esta a√ß√£o n√£o pode ser desfeita!
                </div>
              </div>
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancelar</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => {
                setShowConfirmDialog(false);
                handleRecreateWithAnimation();
              }}
              className="bg-orange-600 hover:bg-orange-700"
            >
              Sim, Recriar Checkpoints
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      {/* Progress Dialog */}
      <Dialog open={isRecreating} onOpenChange={() => {}}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <RefreshCw className="h-5 w-5 animate-spin" />
              Recriando Checkpoints
            </DialogTitle>
            <DialogDescription>
              Aguarde enquanto os checkpoints s√£o recriados...
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <Progress value={recreateProgress} className="w-full" />
            <div className="text-center">
              <p className="text-sm text-muted-foreground">
                {recreateProgress < 100 ? `${Math.round(recreateProgress)}% conclu√≠do` : 'Finalizando...'}
              </p>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Calendar, TrendingUp, TrendingDown, Minus, CheckCircle2, AlertCircle } from "lucide-react";
import { format } from "date-fns";
import { ptBR } from "date-fns/locale";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { NumberInputBR } from "@/components/ui/number-input-br";
import { parseDecimalBR, formatBrazilianNumber } from "@/lib/formatters";
import { getProgressBadgeVariant, getProgressClassName } from "@/lib/checkpoint-utils";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";

interface CheckpointUpdaterProps {
  keyResultId?: number;
}

export default function CheckpointUpdater({ keyResultId }: CheckpointUpdaterProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedCheckpoint, setSelectedCheckpoint] = useState<any>(null);
  const [actualValue, setActualValue] = useState("");
  const [notes, setNotes] = useState("");

  const { data: checkpoints, isLoading } = useQuery({
    queryKey: ["/api/checkpoints", keyResultId],
    queryFn: async () => {
      const params = keyResultId ? `?keyResultId=${keyResultId}` : "";
      const response = await fetch(`/api/checkpoints${params}`);
      if (!response.ok) throw new Error("Erro ao carregar checkpoints");
      return response.json();
    },
  });

  const { data: keyResults } = useQuery({
    queryKey: ["/api/key-results"],
  });

  const updateMutation = useMutation({
    mutationFn: async (data: { id: number; actualValue: string; notes: string }) => {
      await apiRequest("PUT", `/api/checkpoints/${data.id}`, {
        actualValue: parseDecimalBR(data.actualValue), // Converte formato brasileiro
        notes: data.notes,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/checkpoints"] });
      queryClient.invalidateQueries({ queryKey: ["/api/key-results"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/kpis"] });
      toast({
        title: "Checkpoint atualizado",
        description: "O checkpoint foi atualizado com sucesso.",
      });
      setSelectedCheckpoint(null);
      setActualValue("");
      setNotes("");
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "Erro ao atualizar checkpoint.",
        variant: "destructive",
      });
    },
  });

  const getCheckpointStatus = (checkpoint: any) => {
    if (checkpoint.status === "completed") return "completed";
    const today = new Date();
    const checkpointDate = new Date(checkpoint.period);
    if (today > checkpointDate) return "overdue";
    return "pending";
  };

  const getProgressIcon = (current: number, previous: number) => {
    if (current > previous) return <TrendingUp className="h-4 w-4 text-green-600" />;
    if (current < previous) return <TrendingDown className="h-4 w-4 text-red-600" />;
    return <Minus className="h-4 w-4 text-gray-400" />;
  };

  const groupedCheckpoints = checkpoints?.reduce((acc: any, checkpoint: any) => {
    const kr = keyResults?.find ? keyResults.find((k: any) => k.id === checkpoint.keyResultId) : null;
    if (!kr) return acc;

    if (!acc[checkpoint.keyResultId]) {
      acc[checkpoint.keyResultId] = {
        keyResult: kr,
        checkpoints: [],
      };
    }
    acc[checkpoint.keyResultId].checkpoints.push(checkpoint);
    return acc;
  }, {});

  const handleUpdate = () => {
    if (!selectedCheckpoint || !actualValue) return;

    updateMutation.mutate({
      id: selectedCheckpoint.id,
      actualValue,
      notes,
    });
  };

  if (isLoading) {
    return <div className="text-center py-8">Carregando checkpoints...</div>;
  }

  return (
    <>
      <div className="space-y-6">
        {Object.values(groupedCheckpoints || {}).map((group: any) => {
          const pendingCheckpoints = group.checkpoints.filter(
            (cp: any) => getCheckpointStatus(cp) !== "completed"
          );
          const completedCheckpoints = group.checkpoints.filter(
            (cp: any) => getCheckpointStatus(cp) === "completed"
          );

          return (
            <Card key={group.keyResult.id}>
              <CardHeader>
                <CardTitle className="text-lg">{group.keyResult.title}</CardTitle>
                <CardDescription>
                  Meta: {group.keyResult.targetValue} {group.keyResult.unit} | 
                  Progresso Atual: {group.keyResult.progress}%
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="mb-4">
                  <Progress value={parseFloat(group.keyResult.progress)} className="h-2" />
                </div>

                <div className="space-y-4">
                  <div>
                    <h4 className="font-medium mb-2 text-sm text-gray-700">
                      Checkpoints Pendentes ({pendingCheckpoints.length})
                    </h4>
                    <div className="space-y-2">
                      {pendingCheckpoints.map((checkpoint: any, index: number) => {
                        const status = getCheckpointStatus(checkpoint);
                        const previousCheckpoint = group.checkpoints[index - 1];

                        return (
                          <div
                            key={checkpoint.id}
                            className={`p-3 rounded-lg border cursor-pointer transition-colors ${
                              status === "overdue"
                                ? "border-red-200 bg-red-50 hover:bg-red-100"
                                : "border-gray-200 hover:bg-gray-50"
                            }`}
                            onClick={() => {
                              setSelectedCheckpoint(checkpoint);
                              setActualValue(checkpoint.actualValue || "");
                              setNotes(checkpoint.notes || "");
                            }}
                          >
                            <div className="flex items-center justify-between">
                              <div className="flex items-center gap-3">
                                <Calendar className={`h-4 w-4 ${
                                  status === "overdue" ? "text-red-600" : "text-gray-400"
                                }`} />
                                <div>
                                  <p className="font-medium text-sm">{checkpoint.period}</p>
                                  <p className="text-xs text-gray-500">
                                    Meta: {checkpoint.targetValue} {group.keyResult.unit}
                                  </p>
                                </div>
                              </div>
                              <div className="text-right">
                                {checkpoint.actualValue ? (
                                  <div className="flex items-center gap-2">
                                    <span className="text-sm font-medium">
                                      {checkpoint.actualValue}
                                    </span>
                                    {previousCheckpoint?.actualValue &&
                                      getProgressIcon(
                                        parseFloat(checkpoint.actualValue),
                                        parseFloat(previousCheckpoint.actualValue)
                                      )}
                                  </div>
                                ) : (
                                  <Badge variant="outline" className="text-xs">
                                    {status === "overdue" ? "Atrasado" : "Aguardando"}
                                  </Badge>
                                )}
                              </div>
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  </div>

                  {completedCheckpoints.length > 0 && (
                    <div>
                      <h4 className="font-medium mb-2 text-sm text-gray-700">
                        Checkpoints Conclu√≠dos ({completedCheckpoints.length})
                      </h4>
                      <div className="space-y-2">
                        {completedCheckpoints.map((checkpoint: any) => {
                          const progress = parseFloat(checkpoint.progress) || 0;
                          const badgeVariant = getProgressBadgeVariant(progress);
                          const progressClasses = getProgressClassName(progress);

                          return (
                            <div
                              key={checkpoint.id}
                              className={`p-3 rounded-lg border ${progressClasses}`}
                            >
                              <div className="flex items-center justify-between">
                                <div className="flex items-center gap-3">
                                  <CheckCircle2 className={`h-4 w-4 ${
                                    progress >= 100 ? 'text-green-600' : 
                                    progress >= 85 ? 'text-yellow-600' : 'text-red-600'
                                  }`} />
                                  <div>
                                    <p className="font-medium text-sm">{checkpoint.period}</p>
                                    <p className="text-xs text-gray-500">
                                      Conclu√≠do em {format(new Date(checkpoint.completedAt), "dd/MM/yyyy")}
                                    </p>
                                  </div>
                                </div>
                                <div className="text-right flex items-center gap-2">
                                  <div>
                                    <p className="text-sm font-medium">
                                      {checkpoint.actualValue} / {checkpoint.targetValue}
                                    </p>
                                    <p className="text-xs text-gray-500">
                                      {progress.toFixed(1)}% da meta
                                    </p>
                                  </div>
                                  <Badge variant={badgeVariant} className="text-xs">
                                    {progress >= 100 ? "Superou" : 
                                     progress >= 85 ? "Quase l√°" : "Abaixo"}
                                  </Badge>
                                </div>
                              </div>
                              {checkpoint.notes && (
                                <p className="text-xs text-gray-600 mt-2">{checkpoint.notes}</p>
                              )}
                            </div>
                          );
                        })}
                      </div>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      <Dialog
        open={!!selectedCheckpoint}
        onOpenChange={(open) => {
          if (!open) {
            setSelectedCheckpoint(null);
            setActualValue("");
            setNotes("");
          }
        }}
      >
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Atualizar Checkpoint</DialogTitle>
            <DialogDescription>
              Registre o progresso para o per√≠odo {selectedCheckpoint?.period}
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            <div>
              <Label htmlFor="actualValue">Valor Atual *</Label>
              <NumberInputBR
                id="actualValue"
                value={actualValue}
                onChange={setActualValue}
                placeholder="0,00"
                decimals={2}
              />
              <p className="text-sm text-gray-500 mt-1">
                Meta: {selectedCheckpoint?.targetValue} {selectedCheckpoint?.keyResult?.unit}
              </p>
            </div>

            <div>
              <Label htmlFor="notes">Observa√ß√µes</Label>
              <Textarea
                id="notes"
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                placeholder="Adicione observa√ß√µes sobre o progresso..."
                rows={3}
              />
            </div>

            <div className="flex justify-end gap-2">
              <Button
                variant="outline"
                onClick={() => {
                  setSelectedCheckpoint(null);
                  setActualValue("");
                  setNotes("");
                }}
              >
                Cancelar
              </Button>
              <Button
                onClick={handleUpdate}
                disabled={!actualValue || updateMutation.isPending}
              >
                {updateMutation.isPending ? "Salvando..." : "Salvar"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

