import { useMemo } from "react";
import { motion } from "framer-motion";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { AlertTriangle, Calendar, Target, TrendingUp, Clock, ChevronRight } from "lucide-react";
import { format, isAfter, isBefore, addDays } from "date-fns";
import { ptBR } from "date-fns/locale";

interface NextCheckpointsOverviewProps {
  checkpoints: any[];
  keyResults: any[];
  onCheckpointClick: (checkpoint: any) => void;
  onSelectKeyResult: (keyResultId: number) => void;
}

export default function NextCheckpointsOverview({
  checkpoints,
  keyResults,
  onCheckpointClick,
  onSelectKeyResult
}: NextCheckpointsOverviewProps) {
  const checkpointData = useMemo(() => {
    if (!checkpoints || !keyResults) return { upcoming: [], overdue: [] };

    const now = new Date();
    const in7Days = addDays(now, 7);

    // Enrich checkpoints with key result data
    const enrichedCheckpoints = checkpoints.map(checkpoint => {
      const keyResult = keyResults.find(kr => kr.id === checkpoint.keyResultId);
      return {
        ...checkpoint,
        keyResult: keyResult || checkpoint.keyResult,
        dueDate: new Date(checkpoint.dueDate)
      };
    });

    // Filter upcoming checkpoints (next 7 days, not completed)
    const upcoming = enrichedCheckpoints
      .filter(cp => 
        cp.status !== 'completed' &&
        isAfter(cp.dueDate, now) &&
        isBefore(cp.dueDate, in7Days)
      )
      .sort((a, b) => a.dueDate.getTime() - b.dueDate.getTime())
      .slice(0, 5);

    // Filter overdue checkpoints
    const overdue = enrichedCheckpoints
      .filter(cp => 
        cp.status !== 'completed' &&
        isBefore(cp.dueDate, now)
      )
      .sort((a, b) => a.dueDate.getTime() - b.dueDate.getTime())
      .slice(0, 5);

    return { upcoming, overdue };
  }, [checkpoints, keyResults]);

  const getStatusColor = (checkpoint: any) => {
    const isOverdue = isBefore(checkpoint.dueDate, new Date()) && checkpoint.status !== 'completed';
    if (isOverdue) return 'border-red-200 bg-red-50';
    if (checkpoint.status === 'in_progress') return 'border-blue-200 bg-blue-50';
    return 'border-gray-200 bg-white';
  };

  const getUrgencyBadge = (checkpoint: any) => {
    const isOverdue = isBefore(checkpoint.dueDate, new Date()) && checkpoint.status !== 'completed';
    const daysUntilDue = Math.ceil((checkpoint.dueDate.getTime() - new Date().getTime()) / (1000 * 60 * 60 * 24));

    if (isOverdue) {
      return <Badge variant="destructive">Atrasado</Badge>;
    }
    if (daysUntilDue <= 1) {
      return <Badge variant="destructive">Vence hoje</Badge>;
    }
    if (daysUntilDue <= 3) {
      return <Badge variant="secondary" className="bg-orange-100 text-orange-800">Urgente</Badge>;
    }
    return <Badge variant="outline">Pr√≥ximo</Badge>;
  };

  const CheckpointCard = ({ checkpoint }: { checkpoint: any }) => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className={`p-4 rounded-lg border cursor-pointer hover:shadow-md transition-all duration-200 ${getStatusColor(checkpoint)}`}
      onClick={() => onCheckpointClick(checkpoint)}
    >
      <div className="space-y-3">
        <div className="flex items-start justify-between">
          <div className="space-y-1 flex-1">
            <div className="flex items-center gap-2">
              <h4 className="font-medium text-sm text-gray-900">
                {checkpoint.title}
              </h4>
              {getUrgencyBadge(checkpoint)}
            </div>

            <p className="text-xs text-gray-600 line-clamp-1">
              {checkpoint.keyResult?.title}
            </p>
          </div>

          <ChevronRight className="h-4 w-4 text-gray-400 flex-shrink-0" />
        </div>

        <div className="flex items-center justify-between text-xs text-gray-500">
          <div className="flex items-center gap-1">
            <Calendar className="h-3 w-3" />
            {format(checkpoint.dueDate, 'dd/MM/yyyy', { locale: ptBR })}
          </div>

          <div className="flex items-center gap-1">
            <Target className="h-3 w-3" />
            {checkpoint.actualValue || 0}/{checkpoint.targetValue} {checkpoint.keyResult?.unit}
          </div>
        </div>

        {checkpoint.status === 'in_progress' && (
          <div className="w-full bg-gray-200 rounded-full h-1.5">
            <div 
              className="bg-blue-600 h-1.5 rounded-full transition-all duration-300"
              style={{ 
                width: `${Math.min(100, ((parseFloat(checkpoint.actualValue) || 0) / parseFloat(checkpoint.targetValue) || 1) * 100)}%` 
              }}
            />
          </div>
        )}
      </div>
    </motion.div>
  );

  return (
    <div className="space-y-6">
      {/* Overdue Checkpoints */}
      {checkpointData.overdue.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-red-700">
              <AlertTriangle className="h-5 w-5" />
              Checkpoints Atrasados
            </CardTitle>
            <CardDescription>
              {checkpointData.overdue.length} checkpoint{checkpointData.overdue.length !== 1 ? 's' : ''} com prazo vencido
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {checkpointData.overdue.map((checkpoint) => (
                <CheckpointCard key={checkpoint.id} checkpoint={checkpoint} />
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Upcoming Checkpoints */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Clock className="h-5 w-5" />
            Pr√≥ximos Checkpoints
          </CardTitle>
          <CardDescription>
            Checkpoints com prazo nos pr√≥ximos 7 dias
          </CardDescription>
        </CardHeader>
        <CardContent>
          {checkpointData.upcoming.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <Target className="h-12 w-12 mx-auto mb-3 text-gray-300" />
              <p className="text-sm">Nenhum checkpoint pr√≥ximo</p>
              <p className="text-xs text-gray-400 mt-1">
                Todos os checkpoints est√£o em dia!
              </p>
            </div>
          ) : (
            <div className="space-y-3">
              {checkpointData.upcoming.map((checkpoint) => (
                <CheckpointCard key={checkpoint.id} checkpoint={checkpoint} />
              ))}

              {checkpoints.length > 5 && (
                <Button 
                  variant="outline" 
                  className="w-full mt-4"
                  onClick={() => onSelectKeyResult(-1)} // Special value to show all
                >
                  Ver todos os checkpoints
                </Button>
              )}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/objective-form.tsx.content:
import React from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { insertObjectiveSchema } from "@shared/schema";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/use-auth";

const objectiveFormSchema = insertObjectiveSchema.extend({
  title: z.string().min(1, "T√≠tulo √© obrigat√≥rio"),
  startDate: z.string().min(1, "Data de in√≠cio √© obrigat√≥ria"),
  endDate: z.string().min(1, "Data de fim √© obrigat√≥ria"),
});

type ObjectiveFormData = z.infer<typeof objectiveFormSchema>;

interface ObjectiveFormProps {
  objective?: any;
  onSuccess: () => void;
}

export default function ObjectiveForm({ objective, onSuccess }: ObjectiveFormProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<ObjectiveFormData>({
    resolver: zodResolver(objectiveFormSchema),
    defaultValues: {
      title: objective?.title || "",
      description: objective?.description || "",
      ownerId: objective?.ownerId || user?.id,
      regionId: objective?.regionId || undefined,
      subRegionIds: objective?.subRegionIds || (objective?.subRegionId ? [objective.subRegionId] : []),
      startDate: objective?.startDate ? new Date(objective.startDate).toISOString().split('T')[0] : "",
      endDate: objective?.endDate ? new Date(objective.endDate).toISOString().split('T')[0] : "",
    },
  });

  const selectedRegionId = form.watch("regionId");
  const startDate = form.watch("startDate");
  const endDate = form.watch("endDate");

  const { data: regions } = useQuery({
    queryKey: ["/api/regions"],
    queryFn: async () => {
      const response = await fetch("/api/regions", {
        credentials: 'include'
      });
      if (!response.ok) throw new Error("Erro ao carregar regi√µes");
      return response.json();
    },
  });

  const { data: subRegions } = useQuery({
    queryKey: ["/api/sub-regions"],
    queryFn: async () => {
      const response = await fetch("/api/sub-regions", {
        credentials: 'include'
      });
      if (!response.ok) throw new Error("Erro ao carregar sub-regi√µes");
      return response.json();
    },
  });

  // Filter sub-regions based on selected region
  const filteredSubRegions = selectedRegionId && subRegions
    ? subRegions.filter((subRegion: any) => subRegion.regionId === selectedRegionId)
    : [];

  const mutation = useMutation({
    mutationFn: async (data: ObjectiveFormData) => {
      const payload = {
        ...data,
        startDate: data.startDate,
        endDate: data.endDate,
      };

      if (objective) {
        await apiRequest("PUT", `/api/objectives/${objective.id}`, payload);
      } else {
        await apiRequest("POST", "/api/objectives", payload);
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/objectives"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/kpis"] });
      toast({
        title: objective ? "Objetivo atualizado" : "Objetivo criado",
        description: objective 
          ? "O objetivo foi atualizado com sucesso." 
          : "O objetivo foi criado com sucesso.",
      });
      onSuccess();
    },
    onError: (error: any) => {
      toast({
        title: "Erro",
        description: error.message || "Erro ao salvar objetivo.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: ObjectiveFormData) => {
    console.log("üîç Frontend: Dados do formul√°rio para envio:", JSON.stringify(data, null, 2));
    mutation.mutate(data);
  };

  // Function to determine quarter from date (kept for reference but not used)
  const getQuarterFromDate = (dateString: string): string => {
    if (!dateString) return "";
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = date.getMonth() + 1; // getMonth() returns 0-11
    const quarter = Math.ceil(month / 3);
    return `${year}-q${quarter}`;
  };



  return (
    <div>
      <DialogHeader>
        <DialogTitle>
          {objective ? "Editar Objetivo" : "Criar Novo Objetivo"}
        </DialogTitle>
      </DialogHeader>

      <Form {...form}>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6 mt-6">
          <FormField
            control={form.control}
            name="title"
            render={({ field }) => (
              <FormItem>
                <FormLabel>T√≠tulo do Objetivo *</FormLabel>
                <FormControl>
                  <Input placeholder="Digite o t√≠tulo do objetivo" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="description"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Descri√ß√£o</FormLabel>
                <FormControl>
                  <Textarea 
                    placeholder="Descreva o objetivo"
                    className="resize-none"
                    rows={3}
                    {...field}
                    value={field.value || ""}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="regionId"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Regi√£o</FormLabel>
                <Select 
                  onValueChange={(value) => {
                    const regionId = value === "none" ? undefined : parseInt(value);
                    field.onChange(regionId);
                    // Clear sub-regions when region changes
                    form.setValue("subRegionIds", []);
                  }}
                  value={field.value?.toString() || ""}
                >
                  <FormControl>
                    <SelectTrigger>
                      <SelectValue placeholder="Selecione uma regi√£o" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    <SelectItem value="none">Nenhuma regi√£o</SelectItem>
                    {regions && regions.map((region: any) => (
                      <SelectItem key={region.id} value={region.id.toString()}>
                        {region.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />

          {selectedRegionId && filteredSubRegions && filteredSubRegions.length > 0 && (
            <FormField
              control={form.control}
              name="subRegionIds"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Sub-Regi√µes (Opcional - M√∫ltipla Escolha)</FormLabel>
                  <div className="grid grid-cols-2 gap-2 p-4 border rounded-lg">
                    {filteredSubRegions.map((subRegion: any) => (
                      <div key={subRegion.id} className="flex items-center space-x-2">
                        <input
                          type="checkbox"
                          id={`subregion-${subRegion.id}`}
                          checked={Array.isArray(field.value) && field.value.includes(subRegion.id)}
                          onChange={(e) => {
                            const currentValues = Array.isArray(field.value) ? field.value : [];
                            if (e.target.checked) {
                              field.onChange([...currentValues, subRegion.id]);
                            } else {
                              field.onChange(currentValues.filter((id: number) => id !== subRegion.id));
                            }
                          }}
                          className="rounded border-gray-300"
                        />
                        <label htmlFor={`subregion-${subRegion.id}`} className="text-sm font-medium">
                          {subRegion.name}
                        </label>
                      </div>
                    ))}
                  </div>
                  <FormMessage />
                </FormItem>
              )}
            />
          )}



          <div className="grid grid-cols-2 gap-4">
            <FormField
              control={form.control}
              name="startDate"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Data de In√≠cio *</FormLabel>
                  <FormControl>
                    <Input type="date" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="endDate"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Data de Fim *</FormLabel>
                  <FormControl>
                    <Input type="date" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          <div className="flex justify-end space-x-3 pt-6 border-t">
            <Button type="button" variant="outline" onClick={onSuccess}>
              Cancelar
            </Button>
            <Button type="submit" disabled={mutation.isPending}>
              {mutation.isPending 
                ? "Salvando..." 
                : objective 
                  ? "Atualizar Objetivo" 
                  : "Criar Objetivo"
              }
            </Button>
          </div>
        </form>
      </Form>
    </div>
  );
}

client/src/components/objectives-table.t.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Eye, Edit, Trash2, Download, Search, MoreHorizontal } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Skeleton } from "@/components/ui/skeleton";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";
import { Dialog, DialogContent, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import ObjectiveForm from "./objective-form";

interface ObjectivesTableProps {
  objectives?: any[];
  isLoading: boolean;
  showActions?: boolean;
}

export default function ObjectivesTable({ objectives, isLoading, showActions = false }: ObjectivesTableProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedObjective, setSelectedObjective] = useState<any>(null);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const deleteMutation = useMutation({
    mutationFn: async (id: number) => {
      await apiRequest("DELETE", `/api/objectives/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/objectives"] });
      toast({
        title: "Objetivo exclu√≠do",
        description: "O objetivo foi exclu√≠do com sucesso.",
      });
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "Erro ao excluir objetivo.",
        variant: "destructive",
      });
    },
  });

  const filteredObjectives = objectives?.filter((objective) =>
    objective.title?.toLowerCase().includes(searchQuery.toLowerCase()) ||
    objective.description?.toLowerCase().includes(searchQuery.toLowerCase())
  ) || [];

  const getStatusBadge = (progress: number) => {
    if (progress >= 70) {
      return { label: "No prazo", variant: "default" as const, color: "bg-secondary" };
    }
    if (progress >= 40) {
      return { label: "Aten√ß√£o", variant: "secondary" as const, color: "bg-accent" };
    }
    return { label: "Atrasado", variant: "destructive" as const, color: "bg-destructive" };
  };

  const getProgressColor = (progress: number) => {
    if (progress >= 70) return "bg-secondary";
    if (progress >= 40) return "bg-accent";
    return "bg-destructive";
  };

  const getUserInitials = (name: string) => {
    return name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const handleEdit = (objective: any) => {
    setSelectedObjective(objective);
    setIsEditModalOpen(true);
  };

  const handleDelete = (id: number) => {
    deleteMutation.mutate(id);
  };

  const exportData = () => {
    if (!objectives || objectives.length === 0) {
      toast({
        title: "Aviso",
        description: "Nenhum dado para exportar.",
        variant: "destructive",
      });
      return;
    }

    const csvContent = [
      ["T√≠tulo", "Descri√ß√£o", "Regi√£o", "Respons√°vel", "Progresso", "Status"],
      ...objectives.map(obj => [
        obj.title,
        obj.description || "",
        obj.region?.name || "",
        obj.owner?.name || "Sem respons√°vel",
        `${parseFloat(obj.progress || "0").toFixed(1)}%`,
        getStatusBadge(parseFloat(obj.progress || "0")).label
      ])
    ]
    .map(row => row.map(cell => `"${cell}"`).join(","))
    .join("\n");

    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "objetivos.csv";
    link.click();
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle>Objetivos Ativos</CardTitle>
          <div className="flex items-center space-x-3">
            <div className="relative">
              <Search className="absolute left-3 top-2.5 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Buscar objetivos..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10 w-64"
              />
            </div>
            <Button variant="outline" onClick={exportData}>
              <Download className="mr-2 h-4 w-4" />
              Exportar
            </Button>
          </div>
        </div>
      </CardHeader>

      <CardContent>
        {isLoading ? (
          <div className="space-y-4">
            {[1, 2, 3].map((i) => (
              <div key={i} className="flex items-center space-x-4 p-4">
                <Skeleton className="h-12 w-12 rounded" />
                <div className="space-y-2 flex-1">
                  <Skeleton className="h-4 w-1/3" />
                  <Skeleton className="h-3 w-1/2" />
                </div>
                <Skeleton className="h-8 w-20" />
                <Skeleton className="h-8 w-24" />
              </div>
            ))}
          </div>
        ) : (
          <div className="overflow-x-auto">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Objetivo</TableHead>
                  <TableHead>Regi√£o</TableHead>
                  <TableHead>Respons√°vel</TableHead>
                  <TableHead>Progresso</TableHead>
                  <TableHead>Status</TableHead>
                  {showActions && <TableHead>A√ß√µes</TableHead>}
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredObjectives.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={showActions ? 6 : 5} className="text-center py-8">
                      <p className="text-muted-foreground">
                        {searchQuery ? "Nenhum objetivo encontrado para a busca." : "Nenhum objetivo encontrado."}
                      </p>
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredObjectives.map((objective) => {
                    const progress = parseFloat(objective.progress || "0");
                    const statusBadge = getStatusBadge(progress);

                    return (
                      <TableRow key={objective.id} className="hover:bg-muted/50">
                        <TableCell>
                          <div>
                            <p className="font-medium text-foreground">
                              {objective.title}
                            </p>
                            {objective.description && (
                              <p className="text-sm text-muted-foreground mt-1">
                                {objective.description}
                              </p>
                            )}
                          </div>
                        </TableCell>

                        <TableCell className="text-foreground">
                          {(() => {
                            // Priorizar sub-regi√µes m√∫ltiplas, depois sub-regi√£o √∫nica, depois regi√£o
                            if (objective.subRegionIds && Array.isArray(objective.subRegionIds) && objective.subRegionIds.length > 0) {
                              // Buscar nomes das sub-regi√µes m√∫ltiplas (implementar quando tiver dados)
                              return `${objective.regionName} (${objective.subRegionIds.length} sub-regi√µes)`;
                            } else if (objective.subRegionName) {
                              return objective.subRegionName;
                            } else if (objective.regionName) {
                              return objective.regionName;
                            } else {
                              return "N√£o informado";
                            }
                          })()}
                        </TableCell>

                        <TableCell>
                          <div className="flex items-center space-x-3">
                            <Avatar className="h-8 w-8">
                              <AvatarFallback className="text-xs">
                                {getUserInitials(objective.owner?.name || "SR")}
                              </AvatarFallback>
                            </Avatar>
                            <span className="text-sm text-foreground">
                              {objective.owner?.name || "Sem respons√°vel"}
                            </span>
                          </div>
                        </TableCell>

                        <TableCell>
                          <div className="flex items-center space-x-3">
                            <div className="w-16 bg-muted rounded-full h-2">
                              <div 
                                className={`h-2 rounded-full transition-all duration-300 ${getProgressColor(progress)}`}
                                style={{ width: `${Math.min(progress, 100)}%` }}
                              />
                            </div>
                            <span className="text-sm text-muted-foreground min-w-[3rem]">
                              {progress.toFixed(1)}%
                            </span>
                          </div>
                        </TableCell>

                        <TableCell>
                          <Badge variant={statusBadge.variant}>
                            {statusBadge.label}
                          </Badge>
                        </TableCell>

                        {showActions && (
                          <TableCell className="relative">
                            <div className="flex justify-end">
                              <DropdownMenu>
                                <DropdownMenuTrigger asChild>
                                  <Button variant="ghost" size="sm">
                                    <MoreHorizontal className="h-4 w-4" />
                                  </Button>
                                </DropdownMenuTrigger>
                                <DropdownMenuContent align="end" className="z-[9999]">
                                  <DropdownMenuItem>
                                    <Eye className="mr-2 h-4 w-4" />
                                    Visualizar
                                  </DropdownMenuItem>
                                  <DropdownMenuItem onClick={() => handleEdit(objective)}>
                                    <Edit className="mr-2 h-4 w-4" />
                                    Editar
                                  </DropdownMenuItem>
                                  <AlertDialog>
                                    <AlertDialogTrigger asChild>
                                      <DropdownMenuItem onSelect={(e) => e.preventDefault()}>
                                        <Trash2 className="mr-2 h-4 w-4" />
                                        Deletar
                                      </DropdownMenuItem>
                                    </AlertDialogTrigger>
                                    <AlertDialogContent>
                                      <AlertDialogHeader>
                                        <AlertDialogTitle>Confirmar exclus√£o</AlertDialogTitle>
                                        <AlertDialogDescription>
                                          Tem certeza que deseja deletar este objetivo? Esta a√ß√£o n√£o pode ser desfeita.
                                        </AlertDialogDescription>
                                      </AlertDialogHeader>
                                      <AlertDialogFooter>
                                        <AlertDialogCancel>Cancelar</AlertDialogCancel>
                                        <AlertDialogAction 
                                          onClick={() => handleDelete(objective.id)}
                                          className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                                        >
                                          Deletar
                                        </AlertDialogAction>
                                      </AlertDialogFooter>
                                    </AlertDialogContent>
                                  </AlertDialog>
                                </DropdownMenuContent>
                              </DropdownMenu>
                            </div>
                          </TableCell>
                        )}
                      </TableRow>
                    );
                  })
                )}
              </TableBody>
            </Table>
          </div>
        )}

        {/* Pagination would go here if needed */}
        {filteredObjectives.length > 0 && (
          <div className="flex items-center justify-between pt-4 border-t">
            <div className="text-sm text-muted-foreground">
              Mostrando {filteredObjectives.length} de {objectives?.length || 0} objetivos
            </div>
          </div>
        )}
      </CardContent>

      {/* Edit Modal */}
      {selectedObjective && (
        <Dialog open={isEditModalOpen} onOpenChange={setIsEditModalOpen}>
          <DialogContent className="max-w-2xl">
            <ObjectiveForm 
              objective={selectedObjective}
              onSuccess={() => {
                setIsEditModalOpen(false);
                setSelectedObjective(null);
              }}
            />
          </DialogContent>
        </Dialog>
      )}
    </Card>
  );
}

client/src/components/progress-chart.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, LineChart, Line } from "recharts";
import { useState } from "react";

interface ProgressChartProps {
  objectives?: any[];
}

export default function ProgressChart({ objectives }: ProgressChartProps) {
  const [chartType, setChartType] = useState("progress");

  if (!objectives || objectives.length === 0) {
    return (
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Progresso dos Objetivos</CardTitle>
            <Select value={chartType} onValueChange={setChartType}>
              <SelectTrigger className="w-48">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="progress">Progresso por Objetivo</SelectItem>
                <SelectItem value="region">Progresso por Regi√£o</SelectItem>
                <SelectItem value="timeline">Linha do Tempo</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardHeader>
        <CardContent>
          <div className="h-64 bg-muted rounded-lg flex items-center justify-center border-2 border-dashed border-muted-foreground/25">
            <div className="text-center text-muted-foreground">
              <BarChart className="h-12 w-12 mx-auto mb-2" />
              <p>Nenhum dado dispon√≠vel para exibir</p>
              <p className="text-sm">Crie objetivos para visualizar o progresso</p>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  // Prepare data based on chart type
  const getChartData = () => {
    if (!objectives || objectives.length === 0) return [];

    switch (chartType) {
      case "progress":
        return objectives.map((obj, index) => {
          const progress = obj?.progress ? parseFloat(obj.progress) : 0;
          return {
            name: `Obj ${index + 1}`,
            fullName: obj?.title || `Objetivo ${index + 1}`,
            progress: isNaN(progress) ? 0 : progress,
            fill: progress >= 70 ? "#388E3C" : 
                  progress >= 40 ? "#F57C00" : "#D32F2F"
          };
        });

      case "region":
        const regionData = objectives.reduce((acc: any, obj) => {
          const regionName = obj?.region?.name || obj?.subRegion?.name || "Sem regi√£o";
          if (!acc[regionName]) {
            acc[regionName] = { total: 0, sum: 0 };
          }
          acc[regionName].total += 1;
          const progress = obj?.progress ? parseFloat(obj.progress) : 0;
          acc[regionName].sum += isNaN(progress) ? 0 : progress;
          return acc;
        }, {});

        return Object.entries(regionData).map(([name, data]: [string, any]) => ({
          name: name.length > 15 ? name.substring(0, 15) + "..." : name,
          fullName: name,
          progress: data.total > 0 ? data.sum / data.total : 0,
          count: data.total,
          fill: "#1976D2"
        }));

      case "timeline":
        const monthlyData = objectives.reduce((acc: any, obj) => {
          try {
            const createdAt = obj?.createdAt || new Date().toISOString();
            const month = new Date(createdAt).toISOString().substring(0, 7);
            if (!acc[month]) {
              acc[month] = { created: 0, avgProgress: 0, totalProgress: 0 };
            }
            acc[month].created += 1;
            const progress = obj?.progress ? parseFloat(obj.progress) : 0;
            acc[month].totalProgress += isNaN(progress) ? 0 : progress;
            acc[month].avgProgress = acc[month].created > 0 ? acc[month].totalProgress / acc[month].created : 0;
            return acc;
          } catch (error) {
            console.warn('Error processing timeline data for objective:', obj);
            return acc;
          }
        }, {});

        return Object.entries(monthlyData)
          .sort(([a], [b]) => a.localeCompare(b))
          .slice(-6) // Last 6 months
          .map(([month, data]: [string, any]) => ({
            name: new Date(month + "-01").toLocaleDateString("pt-BR", { month: "short", year: "2-digit" }),
            created: data.created || 0,
            progress: data.avgProgress || 0
          }));

      default:
        return [];
    }
  };

  const chartData = getChartData();

  const CustomTooltip = ({ active, payload, label }: any) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      const progress = data?.progress ?? 0;
      return (
        <div className="bg-background border border-border rounded-lg shadow-lg p-3">
          <p className="text-sm font-medium">{data.fullName || label}</p>
          {chartType === "progress" && (
            <p className="text-sm text-muted-foreground">
              Progresso: {(payload[0]?.value || 0).toFixed(1).replace('.', ',')}%
            </p>
          )}
          {chartType === "region" && (
            <>
              <p className="text-sm text-muted-foreground">
                Progresso m√©dio: {(payload[0]?.value || 0).toFixed(1)}%
              </p>
              <p className="text-sm text-muted-foreground">
                Objetivos: {data?.count || 0}
              </p>
            </>
          )}
          {chartType === "timeline" && (
            <>
              <p className="text-sm text-muted-foreground">
                Criados: {data?.created || 0}
              </p>
              <p className="text-sm text-muted-foreground">
                Progresso m√©dio: {(data?.progress || 0).toFixed(1)}%
              </p>
            </>
          )}
        </div>
      );
    }
    return null;
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle>Progresso dos Objetivos</CardTitle>
          <Select value={chartType} onValueChange={setChartType}>
            <SelectTrigger className="w-48">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="progress">Progresso por Objetivo</SelectItem>
              <SelectItem value="region">Progresso por Regi√£o</SelectItem>
              <SelectItem value="timeline">Linha do Tempo</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </CardHeader>
      <CardContent>
        <div className="h-64">
          <ResponsiveContainer width="100%" height="100%">
            {chartType === "timeline" ? (
              <LineChart data={chartData}>
                <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
                <XAxis 
                  dataKey="name" 
                  stroke="hsl(var(--muted-foreground))"
                  fontSize={12}
                />
                <YAxis 
                  stroke="hsl(var(--muted-foreground))"
                  fontSize={12}
                />
                <Tooltip content={<CustomTooltip />} />
                <Line 
                  type="monotone" 
                  dataKey="progress" 
                  stroke="hsl(var(--primary))" 
                  strokeWidth={2}
                  dot={{ fill: "hsl(var(--primary))", strokeWidth: 2, r: 4 }}
                />
              </LineChart>
            ) : (
              <BarChart data={chartData}>
                <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
                <XAxis 
                  dataKey="name" 
                  stroke="hsl(var(--muted-foreground))"
                  fontSize={12}
                />
                <YAxis 
                  stroke="hsl(var(--muted-foreground))"
                  fontSize={12}
                  domain={[0, 100]}
                />
                <Tooltip content={<CustomTooltip />} />
                <Bar 
                  dataKey="progress" 
                  radius={[4, 4, 0, 0]}
                />
              </BarChart>
            )}
          </ResponsiveContainer>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/progress-visualiza.content:
import { useState, useEffect } from "react";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, LineChart, Line, PieChart, Pie, Cell } from "recharts";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { TrendingUp, TrendingDown, Target, Activity } from "lucide-react";
import { parseDecimalBR } from "@/lib/formatters";

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8', '#82CA9D', '#FFC658'];

interface ProgressVisualizationProps {
  filters?: {
    regionId?: number;
    subRegionId?: number;
    serviceLineId?: number;
  };
}

export default function ProgressVisualization({ filters }: ProgressVisualizationProps) {
  const [viewType, setViewType] = useState<"overview" | "objectives" | "krs" | "trends">("overview");
  const queryClient = useQueryClient();

  const { data: kpis } = useQuery({
    queryKey: ["/api/dashboard/kpis", JSON.stringify(filters)],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (filters?.regionId) params.append('regionId', filters.regionId.toString());
      if (filters?.subRegionId) params.append('subRegionId', filters.subRegionId.toString());

      const response = await fetch(`/api/dashboard/kpis?${params}`);
      if (!response.ok) throw new Error("Erro ao carregar KPIs");
      return response.json();
    },
  });

  const { data: objectives } = useQuery({
    queryKey: ["/api/objectives", JSON.stringify(filters)],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (filters?.regionId) params.append('regionId', filters.regionId.toString());
      if (filters?.subRegionId) params.append('subRegionId', filters.subRegionId.toString());

      const response = await fetch(`/api/objectives?${params}`);
      if (!response.ok) throw new Error("Erro ao carregar objetivos");
      return response.json();
    },
  });

  const { data: keyResults } = useQuery({
    queryKey: ["/api/key-results", JSON.stringify(filters)],
    queryFn: async () => {
      const response = await fetch("/api/key-results");
      if (!response.ok) throw new Error("Erro ao carregar resultados-chave");
      return response.json();
    },
    staleTime: 0,
  });

  // Force invalidation when filters change
  useEffect(() => {
    console.log('üîÑ ProgressVisualization: Filters changed, invalidating queries:', filters);
    queryClient.invalidateQueries({ queryKey: ["/api/dashboard/kpis"] });
    queryClient.invalidateQueries({ queryKey: ["/api/objectives"] });
    queryClient.invalidateQueries({ queryKey: ["/api/key-results"] });
  }, [filters, queryClient]);

  // Prepare data for charts
  const prepareOverviewData = () => {
    if (!kpis) return [];

    return [
      {
        name: "Objetivos",
        total: kpis.totalObjectives,
        completed: Math.round(kpis.totalObjectives * (kpis.averageProgress / 100)),
        progress: kpis.averageProgress,
      },
      {
        name: "Key Results",
        total: kpis.totalKeyResults,
        completed: Math.round(kpis.totalKeyResults * (kpis.averageProgress / 100)),
        progress: kpis.averageProgress,
      },
      {
        name: "A√ß√µes",
        total: kpis.totalActions,
        completed: kpis.completedActions,
        progress: kpis.totalActions > 0 ? (kpis.completedActions / kpis.totalActions) * 100 : 0,
      },
    ];
  };

  const prepareObjectivesData = () => {
    if (!objectives) return [];

    return objectives.map((obj: any, index: number) => ({
      name: obj.title.length > 20 ? obj.title.substring(0, 20) + "..." : obj.title,
      fullName: obj.title,
      progress: parseFloat(obj.progress || "0"),
      status: obj.status,
      color: COLORS[index % COLORS.length],
    }));
  };

  const prepareKeyResultsData = () => {
    if (!keyResults) return [];

    return keyResults.map((kr: any, index: number) => ({
      name: kr.title.length > 15 ? kr.title.substring(0, 15) + "..." : kr.title,
      fullName: kr.title,
      current: parseDecimalBR(kr.currentValue || "0"),
      target: parseDecimalBR(kr.targetValue || "0"),
      progress: parseDecimalBR(kr.progress || "0"),
      status: kr.status,
      color: COLORS[index % COLORS.length],
    }));
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "completed": return "bg-green-500";
      case "active": return "bg-blue-500";
      case "delayed": return "bg-yellow-500";
      case "cancelled": return "bg-red-500";
      default: return "bg-gray-500";
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case "completed": return "Conclu√≠do";
      case "active": return "Ativo";
      case "delayed": return "Atrasado";
      case "cancelled": return "Cancelado";
      default: return "Pendente";
    }
  };

  const overviewData = prepareOverviewData();
  const objectivesData = prepareObjectivesData();
  const keyResultsData = prepareKeyResultsData();

  return (
    <div className="space-y-6">
      {/* View Type Selector */}
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold">Visualiza√ß√£o de Progresso</h3>
        <Select value={viewType} onValueChange={(value: any) => setViewType(value)}>
          <SelectTrigger className="w-48">
            <SelectValue placeholder="Selecione a visualiza√ß√£o" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="overview">Vis√£o Geral</SelectItem>
            <SelectItem value="objectives">Objetivos</SelectItem>
            <SelectItem value="krs">Resultados-Chave</SelectItem>
            <SelectItem value="trends">Tend√™ncias</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* Overview Charts */}
      {viewType === "overview" && (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Activity className="h-5 w-5" />
                Progresso Geral
              </CardTitle>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={overviewData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="completed" fill="#0088FE" name="Conclu√≠do" />
                  <Bar dataKey="total" fill="#00C49F" name="Total" />
                </BarChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Target className="h-5 w-5" />
                Distribui√ß√£o por Status
              </CardTitle>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie
                    data={overviewData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ name, progress }) => `${name}: ${progress.toFixed(1)}%`}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="progress"
                  >
                    {overviewData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Objectives View */}
      {viewType === "objectives" && (
        <div className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Progresso por Objetivo</CardTitle>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={400}>
                <BarChart data={objectivesData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis domain={[0, 100]} />
                  <Tooltip 
                    formatter={(value, name, props) => [
                      `${value}%`,
                      props.payload.fullName
                    ]}
                  />
                  <Bar dataKey="progress" fill="#0088FE" />
                </BarChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>

          <div className="grid gap-4">
            <h4 className="text-md font-semibold">Detalhes dos Objetivos</h4>
            {objectivesData.map((obj: any, index: number) => (
              <Card key={index}>
                <CardContent className="pt-6">
                  <div className="flex items-center justify-between mb-2">
                    <h5 className="font-medium">{obj.fullName}</h5>
                    <Badge className={getStatusColor(obj.status)}>
                      {getStatusLabel(obj.status)}
                    </Badge>
                  </div>
                  <div className="space-y-2">
                    <div className="flex justify-between text-sm">
                      <span>Progresso</span>
                      <span>{obj.progress.toFixed(1)}%</span>
                    </div>
                    <Progress value={obj.progress} className="h-2" />
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {/* Key Results View */}
      {viewType === "krs" && (
        <div className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Progresso dos Resultados-Chave</CardTitle>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={400}>
                <BarChart data={keyResultsData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip 
                    formatter={(value, name, props) => [
                      value,
                      name === "current" ? "Atual" : "Meta",
                      props.payload.fullName
                    ]}
                  />
                  <Bar dataKey="current" fill="#0088FE" name="Valor Atual" />
                  <Bar dataKey="target" fill="#00C49F" name="Valor Meta" />
                </BarChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>

          <div className="grid gap-4">
            <h4 className="text-md font-semibold">Detalhes dos Resultados-Chave</h4>
            {keyResultsData.map((kr: any, index: number) => (
              <Card key={index}>
                <CardContent className="pt-6">
                  <div className="flex items-center justify-between mb-2">
                    <h5 className="font-medium">{kr.fullName}</h5>
                    <Badge className={getStatusColor(kr.status)}>
                      {getStatusLabel(kr.status)}
                    </Badge>
                  </div>
                  <div className="grid grid-cols-3 gap-4 text-sm">
                    <div>
                      <span className="text-muted-foreground">Atual:</span>
                      <p className="font-semibold">{kr.current}</p>
                    </div>
                    <div>
                      <span className="text-muted-foreground">Meta:</span>
                      <p className="font-semibold">{kr.target}</p>
                    </div>
                    <div>
                      <span className="text-muted-foreground">Progresso:</span>
                      <p className="font-semibold">{kr.progress.toFixed(1)}%</p>
                    </div>
                  </div>
                  <div className="mt-3">
                    <Progress value={kr.progress} className="h-2" />
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {/* Trends View */}
      {viewType === "trends" && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TrendingUp className="h-5 w-5" />
              Tend√™ncias de Progresso
            </CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={400}>
              <LineChart data={objectivesData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis domain={[0, 100]} />
                <Tooltip />
                <Line 
                  type="monotone" 
                  dataKey="progress" 
                  stroke="#0088FE" 
                  strokeWidth={2}
                  dot={{ fill: "#0088FE", strokeWidth: 2, r: 4 }}
                />
              </LineChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/quarterly-filter.t.content:
import { useQuery } from "@tanstack/react-query";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Calendar } from "lucide-react";
import { useQuarterlyFilter } from "@/hooks/use-quarterly-filter";

interface QuarterlyFilterProps {
  variant?: "header" | "card" | "compact" | "sidebar";
  className?: string;
}

export default function QuarterlyFilter({ variant = "header", className = "" }: QuarterlyFilterProps) {
  const { selectedQuarter, setSelectedQuarter } = useQuarterlyFilter();

  const { data: availableQuarters } = useQuery({
    queryKey: ["/api/quarters"],
    queryFn: async () => {
      const response = await fetch("/api/quarters", { credentials: "include" });
      if (!response.ok) throw new Error("Erro ao carregar trimestres");
      return response.json();
    }
  });

  // Different styles based on variant
  const getStyles = () => {
    switch (variant) {
      case "header":
        return {
          trigger: "w-64 bg-white/10 border-white/20 text-white backdrop-blur-sm",
          placeholder: "üóìÔ∏è Todos os per√≠odos"
        };
      case "card":
        return {
          trigger: "w-full",
          placeholder: "üìÖ Filtrar por per√≠odo"
        };
      case "compact":
        return {
          trigger: "w-48",
          placeholder: "Per√≠odo"
        };
      case "sidebar":
        return {
          trigger: "w-full bg-sidebar-background border-sidebar-border text-sidebar-foreground hover:bg-sidebar-accent",
          placeholder: "Todos os per√≠odos"
        };
      default:
        return {
          trigger: "w-64",
          placeholder: "üóìÔ∏è Todos os per√≠odos"
        };
    }
  };

  const styles = getStyles();

  return (
    <div className={`flex items-center gap-2 ${className}`}>
      {variant === "card" && <Calendar className="h-4 w-4 text-muted-foreground" />}
      <Select value={selectedQuarter} onValueChange={setSelectedQuarter}>
        <SelectTrigger className={styles.trigger}>
          <SelectValue placeholder={styles.placeholder} />
        </SelectTrigger>
        <SelectContent>
          <SelectItem value="all">Todos os per√≠odos</SelectItem>
          {availableQuarters?.map((quarter: any) => {
            // Handle both string and object formats
            const quarterValue = typeof quarter === 'string' ? quarter : quarter.id;
            const quarterDisplay = typeof quarter === 'string' && quarter.includes('-T')
              ? (() => {
                  if (typeof quarter === 'string' && quarter.includes('-T')) {
                    const [year, q] = quarter.split('-T');
                    const quarterNames = ['1¬∫ Trimestre', '2¬∫ Trimestre', '3¬∫ Trimestre', '4¬∫ Trimestre'];
                    return `${quarterNames[parseInt(q) - 1]} ${year}`;
                  }
                  return quarter;
                })()
              : (quarter?.name || quarterValue || quarter);

            return (
              <SelectItem key={quarterValue} value={quarterValue}>
                {quarterDisplay}
              </SelectItem>
            );
          })}
        </SelectContent>
      </Select>
    </div>
  );
}

client/src/components/recent-activities..content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Plus, CheckCircle, Edit, Trash, Flag } from "lucide-react";

export default function RecentActivities() {
  const { data: activities, isLoading } = useQuery({
    queryKey: ["/api/activities"],
    queryFn: async () => {
      const response = await fetch("/api/activities?limit=10");
      if (!response.ok) throw new Error("Erro ao carregar atividades");
      return response.json();
    },
  });

  const getActivityIcon = (action: string) => {
    switch (action) {
      case "created":
        return Plus;
      case "updated":
        return Edit;
      case "deleted":
        return Trash;
      case "completed":
        return CheckCircle;
      default:
        return Flag;
    }
  };

  const getActivityColor = (action: string) => {
    switch (action) {
      case "created":
        return "bg-blue-100 text-primary";
      case "updated":
        return "bg-orange-100 text-accent";
      case "deleted":
        return "bg-red-100 text-destructive";
      case "completed":
        return "bg-green-100 text-secondary";
      default:
        return "bg-purple-100 text-purple-600";
    }
  };

  const getUserInitials = (name: string) => {
    return name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const formatTimeAgo = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60));

    if (diffInHours === 0) {
      const diffInMinutes = Math.floor((now.getTime() - date.getTime()) / (1000 * 60));
      return diffInMinutes <= 1 ? "agora" : `h√° ${diffInMinutes} min`;
    }

    if (diffInHours < 24) {
      return `h√° ${diffInHours} hora${diffInHours > 1 ? 's' : ''}`;
    }

    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) {
      return `h√° ${diffInDays} dia${diffInDays > 1 ? 's' : ''}`;
    }

    return date.toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle>Atividades Recentes</CardTitle>
      </CardHeader>
      <CardContent>
        {isLoading ? (
          <div className="space-y-4">
            {[1, 2, 3, 4].map((i) => (
              <div key={i} className="flex items-start space-x-3">
                <Skeleton className="w-8 h-8 rounded-full" />
                <div className="flex-1 space-y-2">
                  <Skeleton className="h-4 w-full" />
                  <Skeleton className="h-3 w-1/2" />
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="space-y-4">
            {activities?.length > 0 ? (
              activities.map((activity: any) => {
                const Icon = getActivityIcon(activity.action);
                const colorClasses = getActivityColor(activity.action);

                return (
                  <div key={activity.id} className="flex items-start space-x-3">
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${colorClasses}`}>
                      <Icon className="h-3 w-3" />
                    </div>
                    <div className="flex-1 min-w-0">
                      <p className="text-sm text-foreground">
                        <span className="font-medium">{activity.user.name}</span>{" "}
                        {activity.description}
                      </p>
                      <p className="text-xs text-muted-foreground mt-1">
                        {formatTimeAgo(activity.createdAt)}
                      </p>
                    </div>
                  </div>
                );
              })
            ) : (
              <div className="text-center py-8">
                <Flag className="h-8 w-8 text-muted-foreground mx-auto mb-2" />
                <p className="text-sm text-muted-foreground">
                  Nenhuma atividade recente
                </p>
              </div>
            )}
          </div>
        )}

        {activities?.length > 0 && (
          <div className="mt-6 pt-4 border-t border-border">
            <Button variant="ghost" className="w-full text-primary hover:bg-primary/10">
              Ver todas as atividades
            </Button>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

