/**
 * Fun√ß√µes utilit√°rias para formata√ß√£o de n√∫meros e datas seguindo padr√£o brasileiro ABNT
 * V√≠rgula como separador decimal, ponto como separador de milhar
 * Formato de data: DD/MM/AAAA, timezone Brasil (UTC-3)
 */

// Configura√ß√µes de timezone e formata√ß√£o para Brasil
const BRAZIL_TIMEZONE = 'America/Sao_Paulo'; // UTC-3
const BRAZIL_LOCALE = 'pt-BR';

/**
 * FUN√á√ïES DE DATA - PADR√ÉO BRASILEIRO
 */

// Formata data para exibi√ß√£o brasileira (DD/MM/AAAA)
export function formatDateBR(date: string | Date): string {
  if (!date) return '';

  let dateObj: Date;
  if (typeof date === 'string') {
    // Se √© uma string ISO (YYYY-MM-DD), criar data sem convers√£o de timezone
    if (date.includes('-') && date.length === 10) {
      const [year, month, day] = date.split('-').map(Number);
      dateObj = new Date(year, month - 1, day); // month √© 0-indexed
    } else {
      dateObj = new Date(date);
    }
  } else {
    dateObj = date;
  }

  if (isNaN(dateObj.getTime())) return '';

  return dateObj.toLocaleDateString(BRAZIL_LOCALE, {
    day: '2-digit',
    month: '2-digit', 
    year: 'numeric'
  });
}

// Formata data e hora para exibi√ß√£o brasileira (DD/MM/AAAA HH:mm)
export function formatDateTimeBR(date: string | Date): string {
  if (!date) return '';

  const dateObj = typeof date === 'string' ? new Date(date) : date;
  if (isNaN(dateObj.getTime())) return '';

  return dateObj.toLocaleString(BRAZIL_LOCALE, {
    timeZone: BRAZIL_TIMEZONE,
    day: '2-digit',
    month: '2-digit',
    year: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  });
}

// Converte data DD/MM/AAAA para formato ISO (AAAA-MM-DD) para backend
export function convertDateBRToISO(dateBR: string): string {
  if (!dateBR) return '';

  const parts = dateBR.split('/');
  if (parts.length !== 3) return '';

  const [day, month, year] = parts;
  return `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
}

// Converte data ISO (AAAA-MM-DD) para formato brasileiro (DD/MM/AAAA)
export function convertISOToDateBR(isoDate: string): string {
  if (!isoDate) return '';

  const parts = isoDate.split('-');
  if (parts.length !== 3) return '';

  const [year, month, day] = parts;
  return `${day}/${month}/${year}`;
}

// Obt√©m data atual no timezone do Brasil
export function getCurrentDateBR(): Date {
  return new Date(new Date().toLocaleString('en-US', { timeZone: BRAZIL_TIMEZONE }));
}

// Valida formato de data brasileira (DD/MM/AAAA)
export function isValidDateBR(dateBR: string): boolean {
  if (!dateBR) return false;

  const regex = /^(\d{2})\/(\d{2})\/(\d{4})$/;
  const match = dateBR.match(regex);

  if (!match) return false;

  const [, day, month, year] = match;
  const date = new Date(parseInt(year), parseInt(month) - 1, parseInt(day));

  return date.getDate() === parseInt(day) &&
         date.getMonth() === parseInt(month) - 1 &&
         date.getFullYear() === parseInt(year);
}

/**
 * FUN√á√ïES DE N√öMERO - PADR√ÉO BRASILEIRO ABNT
 */

// Converte string com v√≠rgula para float (padr√£o brasileiro para banco)
export function parseDecimalBR(value: string | number): number {
  if (!value || value === "") return 0;

  // Se j√° √© n√∫mero, retorna direto
  if (typeof value === "number") return value;

  // Parse inteligente similar ao formatBrazilianNumber
  let cleanValue = value.toString().trim();

  // Se tem ponto E v√≠rgula, assume formato brasileiro: 1.234.567,89
  if (cleanValue.includes('.') && cleanValue.includes(',')) {
    cleanValue = cleanValue.replace(/\./g, "").replace(",", ".");
  }
  // Se s√≥ tem v√≠rgula, assume que √© decimal brasileiro: 1234,56
  else if (cleanValue.includes(',') && !cleanValue.includes('.')) {
    cleanValue = cleanValue.replace(",", ".");
  }
  // Se s√≥ tem ponto, pode ser milhar (1.000) ou decimal (1000.50)
  else if (cleanValue.includes('.') && !cleanValue.includes(',')) {
    const parts = cleanValue.split('.');
    // Se a √∫ltima parte tem 3 d√≠gitos e n√£o h√° outra parte decimal, assume separador de milhar
    if (parts.length === 2 && parts[1].length === 3 && !/\d{4,}/.test(parts[0])) {
      cleanValue = cleanValue.replace(".", "");
    }
    // Sen√£o, mant√©m como decimal internacional
  }

  const parsed = parseFloat(cleanValue);
  return isNaN(parsed) ? 0 : parsed;
}

// FUN√á√ÉO CENTRAL: Formata n√∫meros para padr√£o brasileiro
// Unifica toda a formata√ß√£o em uma s√≥ fun√ß√£o para evitar duplicidade
export function formatBrazilianNumber(value: number | string, decimals?: number): string {
  if (value === null || value === undefined || value === "" || value === "0") return "0";

  let num: number;
  if (typeof value === "string") {
    // Parse inteligente: detecta se √© formato brasileiro ou internacional
    let cleanValue = value.toString().trim();

    // Se tem ponto E v√≠rgula, assume formato brasileiro: 1.234.567,89
    if (cleanValue.includes('.') && cleanValue.includes(',')) {
      // Remove pontos (separadores de milhar) e troca v√≠rgula por ponto
      cleanValue = cleanValue.replace(/\./g, "").replace(",", ".");
    }
    // Se s√≥ tem v√≠rgula, assume que √© decimal brasileiro: 1234,56
    else if (cleanValue.includes(',') && !cleanValue.includes('.')) {
      cleanValue = cleanValue.replace(",", ".");
    }
    // Se s√≥ tem ponto, pode ser milhar (1.000) ou decimal (1000.50)
    else if (cleanValue.includes('.') && !cleanValue.includes(',')) {
      const parts = cleanValue.split('.');
      // Se a √∫ltima parte tem 3 d√≠gitos e n√£o h√° outra parte decimal, assume separador de milhar
      if (parts.length === 2 && parts[1].length === 3 && !/\d{4,}/.test(parts[0])) {
        cleanValue = cleanValue.replace(".", "");
      }
      // Sen√£o, mant√©m como decimal internacional
    }

    num = parseFloat(cleanValue);
  } else {
    num = value;
  }

  if (isNaN(num)) return "0";

  // Se decimals n√£o foi especificado, usar formata√ß√£o inteligente
  if (decimals === undefined) {
    // Se o n√∫mero √© inteiro, n√£o mostrar decimais
    if (num % 1 === 0) {
      return new Intl.NumberFormat('pt-BR', {
        minimumFractionDigits: 0,
        maximumFractionDigits: 0,
      }).format(num);
    } else {
      // Para n√∫meros decimais, detectar automaticamente casas necess√°rias
      const decimalPart = num.toString().split('.')[1] || '';
      const significantDecimals = decimalPart.replace(/0+$/, '').length; // Remove zeros √† direita
      const maxDecimals = Math.max(2, Math.min(significantDecimals, 4)); // Entre 2 e 4 casas

      return new Intl.NumberFormat('pt-BR', {
        minimumFractionDigits: 0,
        maximumFractionDigits: maxDecimals,
      }).format(num);
    }
  }

  // Se decimals foi especificado, usar o valor fornecido
  return new Intl.NumberFormat('pt-BR', {
    minimumFractionDigits: decimals,
    maximumFractionDigits: decimals,
  }).format(num);
}

// ALIASES para manter compatibilidade (todas delegam para a fun√ß√£o central)
export function formatDecimalBR(value: number | string, decimals: number = 2): string {
  return formatBrazilianNumber(value, decimals);
}

export function formatNumberBR(value: number | string, decimals?: number): string {
  return formatBrazilianNumber(value, decimals);
}

// DEPRECATED: Use parseDecimalBR() instead
// Esta fun√ß√£o est√° sendo removida - use parseDecimalBR() para convers√µes adequadas

// Converte valor internacional (com ponto) para valor brasileiro (com v√≠rgula) para exibi√ß√£o
export function convertUSToBR(value: string | number): string {
  if (value === null || value === undefined || value === "") return "0,00";

  const strValue = value.toString();
  return strValue.replace(".", ",");
}

// Valida√ß√£o para campos num√©ricos brasileiros (aceita v√≠rgula e ponto)
export function isValidBRNumber(value: string): boolean {
  if (!value) return true; // Campo vazio √© v√°lido

  // Aceita n√∫meros com v√≠rgula ou ponto como decimal
  const brazilianNumberRegex = /^-?\d+([,.]\d+)?$/;
  return brazilianNumberRegex.test(value.replace(/\s/g, ""));
}

// M√°scara para input num√©rico brasileiro - permite digita√ß√£o livre de n√∫meros
export function maskBRNumber(value: string): string {
  if (!value) return "";

  // Remove tudo que n√£o √© n√∫mero, v√≠rgula ou ponto
  let cleaned = value.replace(/[^\d.,]/g, "");

  // Permite apenas uma v√≠rgula ou ponto como separador decimal
  const decimalSeparators = cleaned.match(/[.,]/g);
  if (decimalSeparators && decimalSeparators.length > 1) {
    // Se h√° m√∫ltiplos separadores, manter apenas o primeiro
    const firstSeparatorIndex = cleaned.search(/[.,]/);
    const beforeSeparator = cleaned.substring(0, firstSeparatorIndex);
    const afterSeparator = cleaned.substring(firstSeparatorIndex + 1).replace(/[.,]/g, "");
    cleaned = beforeSeparator + "," + afterSeparator;
  } else if (decimalSeparators && decimalSeparators.length === 1) {
    // Normalizar separador para v√≠rgula
    cleaned = cleaned.replace(".", ",");
  }

  return cleaned;
}

