  const selectedKeyResult = keyResults?.find((kr: any) => kr.id === selectedKeyResultId);
  const selectedCheckpoints = selectedKeyResultId 
    ? checkpoints?.filter((cp: any) => cp.keyResultId === selectedKeyResultId) || []
    : checkpoints || [];

  return (
