          <div className="max-w-7xl mx-auto space-y-6">
            {/* Header */}
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-3xl font-bold text-gray-900">
                  {selectedKeyResultId ? "Timeline de Checkpoints" : "Checkpoints"}
                </h1>
                <p className="text-gray-600 mt-1">
                  {selectedKeyResultId 
                    ? `Acompanhe o progresso detalhado do resultado-chave` 
                    : "Monitore seus pr√≥ximos checkpoints e prazos"
                  }
                </p>
              </div>

              {selectedKeyResultId && (
                <div className="flex gap-2">
                  <Button 
                    variant="outline" 
                    size="sm"
                    onClick={handleBackToOverview}
                  >
                    <ArrowLeft className="h-4 w-4 mr-2" />
                    Voltar
                  </Button>
                  <div className="flex bg-white rounded-lg border p-1">
                    <Button
                      variant={viewMode === 'circles' ? 'default' : 'ghost'}
                      size="sm"
                      onClick={() => setViewMode('circles')}
                      className="h-8 px-3"
                    >
                      <Grid3X3 className="h-4 w-4" />
                    </Button>
                    <Button
                      variant={viewMode === 'simple' ? 'default' : 'ghost'}
                      size="sm"
                      onClick={() => setViewMode('simple')}
                      className="h-8 px-3"
                    >
                      <List className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              )}
            </div>

            {!selectedKeyResultId ? (
              /* Overview Mode - Show next/overdue checkpoints */
              <>
                {/* Filter Section */}
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <Settings className="h-5 w-5" />
                      Filtros
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="flex gap-4 items-center">
                      <div className="flex-1">
                        <Select 
                          value="overview" 
                          onValueChange={(value) => {
                            if (value !== "overview") {
                              setSelectedKeyResultId(parseInt(value));
                            }
                          }}
                        >
                          <SelectTrigger className="w-full">
                            <SelectValue placeholder="Vis√£o Geral - Pr√≥ximos & Atrasados" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="overview">Vis√£o Geral - Pr√≥ximos & Atrasados</SelectItem>
                            <SelectItem value="separator" disabled className="text-xs font-medium text-gray-500 py-2">
                              ‚îÄ‚îÄ Key Results Espec√≠ficos ‚îÄ‚îÄ
                            </SelectItem>
                            {keyResults?.map((kr: any) => (
                              <SelectItem key={kr.id} value={kr.id.toString()}>
                                <div className="flex items-center gap-2">
                                  <Target className="h-3 w-3" />
                                  {kr.title}
                                </div>
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>

                      {checkpoints && checkpoints.length > 0 && (
                        <Badge variant="secondary" className="whitespace-nowrap">
                          {checkpoints.length} checkpoints
                        </Badge>
                      )}
                    </div>
                  </CardContent>
                </Card>

                {/* Next Checkpoints Overview */}
                <NextCheckpointsOverview
                  checkpoints={checkpoints || []}
                  keyResults={keyResults || []}
                  onCheckpointClick={handleCheckpointClick}
                  onSelectKeyResult={handleSelectKeyResult}
                />
              </>
            ) : (
              /* Detail Mode - Show specific key result timeline */
              <>
                {/* Timeline Header */}
                <CheckpointTimelineHeader
                  keyResult={selectedKeyResult}
                  checkpoints={selectedCheckpoints}
                  onCheckpointClick={handleCheckpointClick}
                />

                {/* Controls */}
                <Card>
                  <CardContent className="py-4">
                    <div className="flex justify-between items-center">
                      <div className="flex items-center gap-4">
                        <Badge variant="outline" className="text-sm">
                          {selectedCheckpoints.length} checkpoints
                        </Badge>
                        <Badge variant="secondary">
                          {selectedCheckpoints.filter((cp: any) => cp.status === 'completed').length} conclu√≠dos
                        </Badge>
                      </div>

                      <Button 
                        onClick={handleRegenerateCheckpoints}
                        variant="outline"
                        size="sm"
                        disabled={createCheckpointsMutation.isPending}
                      >
                        <RotateCcw className="h-4 w-4 mr-2" />
                        {createCheckpointsMutation.isPending ? "Recriando..." : "Recriar Checkpoints"}
                      </Button>
                    </div>
                  </CardContent>
                </Card>

                {/* Checkpoints Display */}
                {viewMode === 'circles' ? (
                  <CheckpointProgressGrid
                    checkpoints={selectedCheckpoints}
                    onCheckpointClick={handleCheckpointClick}
                    onRegenerateCheckpoints={handleRegenerateCheckpoints}
                    keyResultTitle={selectedKeyResult?.title}
                  />
                ) : (
                  /* Simple List View */
                  <Card>
                    <CardHeader>
                      <CardTitle>Lista de Checkpoints</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-3">
                        {selectedCheckpoints.length === 0 ? (
                          <div className="text-center py-8 text-gray-500">
                            <Target className="h-12 w-12 mx-auto mb-3 text-gray-300" />
                            <p>Nenhum checkpoint encontrado</p>
                          </div>
                        ) : (
                          selectedCheckpoints.map((checkpoint: any) => (
                            <div
                              key={checkpoint.id}
                              className="p-4 border rounded-lg hover:bg-gray-50 cursor-pointer transition-colors"
                              onClick={() => handleCheckpointClick(checkpoint)}
                            >
                              <div className="flex items-center justify-between">
                                <div>
                                  <h4 className="font-medium text-gray-900">{checkpoint.title}</h4>
                                  <p className="text-sm text-gray-600">{checkpoint.period}</p>
                                </div>
                                <div className="text-right">
                                  <div className="text-sm font-medium">
                                    {checkpoint.actualValue || 0} / {checkpoint.targetValue}
                                  </div>
                                  <Badge 
                                    variant={checkpoint.status === 'completed' ? 'default' : 'secondary'}
                                    className="mt-1"
                                  >
                                    {checkpoint.status === 'completed' ? 'Conclu√≠do' : 'Pendente'}
                                  </Badge>
                                </div>
                              </div>
                            </div>
                          ))
                        )}
                      </div>
                    </CardContent>
                  </Card>
                )}
              </>
            )}
          </div>
        </div>

      {/* Update Dialog */}
      <CheckpointUpdateDialog
        checkpoint={selectedCheckpoint}
        isOpen={isUpdateDialogOpen}
        onClose={() => {
          setIsUpdateDialogOpen(false);
          setSelectedCheckpoint(null);
        }}
      />
    </div>
  );
}

