// Componente para gerenciar indicadores estrat√©gicos
function StrategicIndicatorsTab() {
  const { toast } = useToast();
  const [editingItem, setEditingItem] = useState<StrategicIndicator | null>(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  const form = useForm<z.infer<typeof strategicIndicatorSchema>>({
    resolver: zodResolver(strategicIndicatorSchema),
    defaultValues: {
      name: "",
      code: "",
      description: "",
      unit: "",
    },
  });

  const { data: indicators = [], isLoading } = useQuery({
    queryKey: ["/api/strategic-indicators"],
    queryFn: async () => {
      const response = await fetch("/api/strategic-indicators");
      if (!response.ok) throw new Error("Falha ao carregar indicadores");
      return response.json();
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: z.infer<typeof strategicIndicatorSchema>) => {
      const response = await fetch("/api/admin/strategic-indicators", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Falha ao criar indicador");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/strategic-indicators"] });
      setIsDialogOpen(false);
      form.reset();
      toast({
        title: "Sucesso",
        description: "Indicador estrat√©gico criado com sucesso",
      });
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "Falha ao criar indicador estrat√©gico",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: z.infer<typeof strategicIndicatorSchema> }) => {
      const response = await fetch(`/api/admin/strategic-indicators/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Falha ao atualizar indicador");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/strategic-indicators"] });
      setIsDialogOpen(false);
      setEditingItem(null);
      form.reset();
      toast({
        title: "Sucesso",
        description: "Indicador estrat√©gico atualizado com sucesso",
      });
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "Falha ao atualizar indicador estrat√©gico",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await fetch(`/api/admin/strategic-indicators/${id}`, {
        method: "DELETE",
      });
      if (!response.ok) throw new Error("Falha ao excluir indicador");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/strategic-indicators"] });
      toast({
        title: "Sucesso",
        description: "Indicador estrat√©gico exclu√≠do com sucesso",
      });
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "Falha ao excluir indicador estrat√©gico",
        variant: "destructive",
      });
    },
  });

  const handleEdit = (indicator: StrategicIndicator) => {
    setEditingItem(indicator);
    form.reset({
      name: indicator.name,
      code: indicator.code || "",
      description: indicator.description || "",
      unit: indicator.unit || "",
    });
    setIsDialogOpen(true);
  };

  const handleSubmit = (data: z.infer<typeof strategicIndicatorSchema>) => {
    if (editingItem) {
      updateMutation.mutate({ id: editingItem.id, data });
    } else {
      createMutation.mutate(data);
    }
  };

  const handleCloseDialog = () => {
    setIsDialogOpen(false);
    setEditingItem(null);
    form.reset();
  };

  if (isLoading) {
    return <div>Carregando indicadores estrat√©gicos...</div>;
  }

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <div>
            <CardTitle>Indicadores Estrat√©gicos</CardTitle>
            <CardDescription>
              Gerencie os indicadores estrat√©gicos utilizados nos resultados-chave
            </CardDescription>
          </div>
          <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
            <DialogTrigger asChild>
              <Button onClick={() => handleCloseDialog()}>
                <Plus className="h-4 w-4 mr-2" />
                Novo Indicador
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>
                  {editingItem ? "Editar Indicador" : "Novo Indicador Estrat√©gico"}
                </DialogTitle>
              </DialogHeader>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Nome *</FormLabel>
                        <FormControl>
                          <Input placeholder="Ex: Taxa de Satisfa√ß√£o" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="code"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>C√≥digo *</FormLabel>
                        <FormControl>
                          <Input placeholder="Ex: TSF001" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Descri√ß√£o</FormLabel>
                        <FormControl>
                          <Textarea 
                            placeholder="Descrii√ß√£o detalhada do indicador..."
                            {...field}
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="unit"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Unidade de Medida</FormLabel>
                        <FormControl>
                          <Input placeholder="Ex: %, pontos, unidades" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <DialogFooter>
                    <Button type="button" variant="outline" onClick={handleCloseDialog}>
                      Cancelar
                    </Button>
                    <Button type="submit" disabled={createMutation.isPending || updateMutation.isPending}>
                      {editingItem ? "Atualizar" : "Criar"}
                    </Button>
                  </DialogFooter>
                </form>
              </Form>
            </DialogContent>
          </Dialog>
        </div>
      </CardHeader>
      <CardContent>
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead>Nome</TableHead>
              <TableHead>C√≥digo</TableHead>
              <TableHead>Descri√ß√£o</TableHead>
              <TableHead>Unidade</TableHead>
              <TableHead className="w-32">A√ß√µes</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {indicators.map((indicator: StrategicIndicator) => (
              <TableRow key={indicator.id}>
                <TableCell className="font-medium">{indicator.name}</TableCell>
                <TableCell>
                  <Badge variant="outline">{indicator.code || "-"}</Badge>
                </TableCell>
                <TableCell>{indicator.description || "-"}</TableCell>
                <TableCell>
                  {indicator.unit ? <Badge variant="secondary">{indicator.unit}</Badge> : "-"}
                </TableCell>
                <TableCell>
                  <div className="flex items-center gap-2">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleEdit(indicator)}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => {
                        if (confirm("Tem certeza que deseja excluir este indicador?")) {
                          deleteMutation.mutate(indicator.id);
                        }
                      }}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </CardContent>
    </Card>
  );
}

// Componente para gerenciar regi√µes e sub-regi√µes
function RegionsTab() {
  const { toast } = useToast();
  const [editingRegion, setEditingRegion] = useState<Region | null>(null);
  const [editingSubRegion, setEditingSubRegion] = useState<SubRegion | null>(null);
  const [isRegionDialogOpen, setIsRegionDialogOpen] = useState(false);
  const [isSubRegionDialogOpen, setIsSubRegionDialogOpen] = useState(false);

  const regionForm = useForm<z.infer<typeof regionSchema>>({
    resolver: zodResolver(regionSchema),
    defaultValues: {
      name: "",
      code: "",
    },
  });

  const subRegionForm = useForm<z.infer<typeof subRegionSchema>>({
    resolver: zodResolver(subRegionSchema),
    defaultValues: {
      name: "",
      code: "",
      regionId: 0,
    },
  });

  const { data: regions = [], isLoading: isLoadingRegions } = useQuery<Region[]>({
    queryKey: ["/api/regions"],
  });

  const { data: subRegions = [], isLoading: isLoadingSubRegions } = useQuery<SubRegion[]>({
    queryKey: ["/api/sub-regions"],
  });

  // Mutations para regi√µes
  const createRegionMutation = useMutation({
    mutationFn: async (data: z.infer<typeof regionSchema>) => {
      const response = await fetch("/api/admin/regions", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Falha ao criar regi√£o");
