import { useState, useEffect } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Calendar, Target, TrendingUp, FileText } from "lucide-react";
import { format } from "date-fns";
import { ptBR } from "date-fns/locale";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { parseDecimalBR } from "@/lib/formatters";
import { cleanupOnDialogClose } from "@/lib/modal-cleanup";

interface CheckpointUpdateDialogProps {
  checkpoint: any;
  isOpen: boolean;
  onClose: () => void;
}

export default function CheckpointUpdateDialog({ 
  checkpoint, 
  isOpen, 
  onClose 
}: CheckpointUpdateDialogProps) {
  const [actualValue, setActualValue] = useState("");
  const [status, setStatus] = useState("");
  const [notes, setNotes] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  useEffect(() => {
    if (checkpoint) {
      // Se o valor √© 0 ou "0", deixar campo vazio para melhor UX
      const currentValue = checkpoint.actualValue;
      setActualValue((currentValue && currentValue !== "0" && currentValue !== 0) ? currentValue : "");
      setStatus(checkpoint.status || "pending");
      setNotes(checkpoint.notes || "");
    }
  }, [checkpoint]);

  const updateCheckpointMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("PUT", `/api/checkpoints/${checkpoint.id}`, data);
    },
    onSuccess: () => {
      toast({
        title: "Checkpoint atualizado",
        description: "As informa√ß√µes do checkpoint foram atualizadas com sucesso.",
      });
      // Invalidate all checkpoint queries and key results to refresh the data
      queryClient.invalidateQueries({ queryKey: ["/api/checkpoints"] });
      queryClient.invalidateQueries({ queryKey: ["/api/key-results"] });
      onClose();
    },
    onError: (error: any) => {
      toast({
        title: "Erro",
        description: error.message || "Erro ao atualizar checkpoint.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const updateData = {
      actualValue: parseDecimalBR(actualValue).toString(), // Convert to database format using proper parser
      status,
      notes: notes.trim() || null,
      completedDate: status === 'completed' ? new Date().toISOString() : null,
      completedAt: status === 'completed' ? new Date().toISOString() : null,
    };

    updateCheckpointMutation.mutate(updateData);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'bg-green-100 text-green-800';
      case 'in_progress': return 'bg-blue-100 text-blue-800';
      case 'pending': return 'bg-gray-100 text-gray-800';
      case 'overdue': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'completed': return 'Conclu√≠do';
      case 'in_progress': return 'Em Progresso';
      case 'pending': return 'Pendente';
      case 'overdue': return 'Atrasado';
      default: return 'Pendente';
    }
  };

  const calculateProgress = () => {
    // CORRE√á√ÉO: Usar parseDecimalBR para converter corretamente valores brasileiros
    const target = parseDecimalBR(checkpoint?.targetValue || '0');
    const actual = parseDecimalBR(actualValue || '0');
    if (target === 0 || !actualValue || actualValue === "") return 0;
    return Math.min(100, Math.round((actual / target) * 100));
  };

  if (!checkpoint) return null;

  const handleDialogChange = (open: boolean) => {
    if (!open) {
      cleanupOnDialogClose();
      onClose();
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleDialogChange}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Target className="h-5 w-5" />
            Atualizar Checkpoint
          </DialogTitle>
          <DialogDescription>
            Atualize o progresso e status do checkpoint
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Checkpoint Info */}
          <div className="space-y-3">
            <div className="flex items-center gap-2 text-sm text-gray-600">
              <Calendar className="h-4 w-4" />
              <span>{checkpoint.title}</span>
            </div>

            <div className="flex items-center gap-2 text-sm text-gray-600">
              <TrendingUp className="h-4 w-4" />
              <span>Meta: {checkpoint.targetValue} {checkpoint.keyResult?.unit}</span>
            </div>

            <div className="flex items-center justify-between">
              <span className="text-sm font-medium">Prazo:</span>
              <Badge variant="outline">
                {format(new Date(checkpoint.dueDate), 'dd/MM/yyyy', { locale: ptBR })}
              </Badge>
            </div>

            <div className="flex items-center justify-between">
              <span className="text-sm font-medium">Status Atual:</span>
              <Badge className={getStatusColor(checkpoint.status)}>
                {getStatusText(checkpoint.status)}
              </Badge>
            </div>
          </div>

          <Separator />

          {/* Update Form */}
          <div className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="actualValue">Valor Atual</Label>
              <Input
                id="actualValue"
                type="text"
                value={actualValue}
                onChange={(e) => setActualValue(e.target.value)}
                placeholder="Digite o valor atual (ex: 20000 ou 20000,50)"
                required
              />
              <div className="text-xs text-gray-500">
                Progresso: {calculateProgress()}% da meta
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="status">Status</Label>
              <Select value={status} onValueChange={setStatus}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="pending">Pendente</SelectItem>
                  <SelectItem value="in_progress">Em Progresso</SelectItem>
                  <SelectItem value="completed">Conclu√≠do</SelectItem>
                  <SelectItem value="overdue">Atrasado</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="notes">Observa√ß√µes</Label>
              <Textarea
                id="notes"
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                placeholder="Adicione observa√ß√µes sobre o progresso..."
                rows={3}
              />
            </div>
          </div>

          {/* Actions */}
          <div className="flex gap-2">
            <Button
              type="button"
              variant="outline"
              onClick={onClose}
              className="flex-1"
            >
              Cancelar
            </Button>
            <Button
              type="submit"
              disabled={updateCheckpointMutation.isPending}
              className="flex-1"
            >
              {updateCheckpointMutation.isPending ? "Salvando..." : "Salvar"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

