import { db } from './mysql-db';
import { keyResults, serviceLines } from '@shared/mysql-schema';
import { eq } from 'drizzle-orm';

async function fixServiceLineAssociations() {
  try {
    console.log('üîß Fixing Service Line Associations for Key Results');

    // Get all service lines first
    const allServiceLines = await db.select().from(serviceLines);
    console.log('üìã Available Service Lines:');
    allServiceLines.forEach(sl => console.log(`  ${sl.id}: ${sl.name}`));

    // Get all key results
    const allKRs = await db.select().from(keyResults);
    console.log(`\nüìã Found ${allKRs.length} Key Results to analyze`);

    // Define associations based on key result content
    const associations = [
      { keyword: 'Vacina√ß√£o', serviceLineId: 15, serviceName: 'Vacina√ß√£o' },
      { keyword: 'Sa√∫de Ocupacional', serviceLineId: 13, serviceName: 'Sa√∫de Ocupacional' },
      { keyword: 'Nutri√ß√£o', serviceLineId: 8, serviceName: 'Nutri√ß√£o' },
      { keyword: 'Atividades F√≠sicas', serviceLineId: 4, serviceName: 'Atividade F√≠sica' },
      { keyword: 'NRs', serviceLineId: 14, serviceName: 'Seguran√ßa do Trabalho' },
      { keyword: 'Pilates', serviceLineId: 4, serviceName: 'Atividade F√≠sica' },
      { keyword: 'bioimped√¢ncia', serviceLineId: 8, serviceName: 'Nutri√ß√£o' }
    ];

    let updatedCount = 0;

    for (const kr of allKRs) {
      let assignedServiceLineId = null;
      let matchedKeyword = '';

      // Find matching association
      for (const assoc of associations) {
        if (kr.title?.toLowerCase().includes(assoc.keyword.toLowerCase())) {
          assignedServiceLineId = assoc.serviceLineId;
          matchedKeyword = assoc.keyword;
          break;
        }
      }

      // Update if we found a match and it's different from current
      if (assignedServiceLineId && kr.serviceLineId !== assignedServiceLineId) {
        await db.update(keyResults)
          .set({ serviceLineId: assignedServiceLineId })
          .where(eq(keyResults.id, kr.id));

        console.log(`‚úÖ Updated KR "${kr.title?.substring(0, 50)}..." -> Service Line ${assignedServiceLineId} (${matchedKeyword})`);
        updatedCount++;
      } else if (assignedServiceLineId && kr.serviceLineId === assignedServiceLineId) {
        console.log(`‚úì KR "${kr.title?.substring(0, 50)}..." already has correct Service Line ${assignedServiceLineId}`);
      } else {
        console.log(`‚ö†Ô∏è  No association found for KR "${kr.title?.substring(0, 50)}..."`);
      }
    }

    console.log(`\nüéâ Fixed ${updatedCount} Key Result associations`);

    // Verify the fix
    console.log('\nüîç Verification - Key Results with "Vacina√ß√£o":');
    const vaccinationKRs = await db.select({
      id: keyResults.id,
      title: keyResults.title,
      serviceLineId: keyResults.serviceLineId
    }).from(keyResults);

    const vacinacaoResults = vaccinationKRs.filter(kr => kr.title?.includes('Vacina√ß√£o'));
    vacinacaoResults.forEach(kr => {
      console.log(`  ${kr.id}: "${kr.title}" -> Service Line ${kr.serviceLineId}`);
    });

  } catch (error) {
    console.error('‚ùå Error fixing service line associations:', error);
  } finally {
    process.exit(0);
  }
}

fixServiceLineAssociations();

client/src/hooks/use-modal-cleanup.ts.content:
import { useEffect } from 'react';
import { forceRemoveAllOverlays } from '@/lib/modal-debug';

/**
 * Hook para limpeza for√ßada de overlays modais que ficam "presos" na DOM
 * ap√≥s fechamento de di√°logos do Radix UI
 */
export function useModalCleanup(isOpen: boolean) {
  useEffect(() => {
    if (!isOpen) {
      // Limpeza imediata + aguarda anima√ß√µes
      const immediateCleanup = () => {
        try {
          // Remove TODOS os overlays de di√°logo com verifica√ß√£o segura
          const allOverlays = document.querySelectorAll('[data-radix-dialog-overlay]');
          allOverlays.forEach(overlay => {
            if (overlay.parentNode) {
              overlay.remove();
            }
          });

          // Remove TODOS os portals de di√°logo com verifica√ß√£o segura
          const allPortals = document.querySelectorAll('[data-radix-dialog-portal]');
          allPortals.forEach(portal => {
            if (portal.parentNode) {
              portal.remove();
            }
          });

          // Remove elementos com position fixed e z-index alto
          const allElements = document.querySelectorAll('*');
          allElements.forEach(element => {
            try {
              const style = window.getComputedStyle(element);
              const zIndex = parseInt(style.zIndex) || 0;
              const position = style.position;

              if (position === 'fixed' && zIndex >= 50 && element.parentNode) {
                const rect = element.getBoundingClientRect();
                // Se cobre toda a tela, provavelmente √© um overlay √≥rf√£o
                if (rect.width >= window.innerWidth - 50 && rect.height >= window.innerHeight - 50) {
                  element.remove();
                }
              }
            } catch (error) {
              // Ignora erros de elementos j√° removidos
            }
          });
        } catch (error) {
          console.log('‚ö†Ô∏è Erro na limpeza imediata:', error);
        }

        // Remove atributos que podem estar bloqueando
        document.body.style.overflow = '';
        document.body.style.paddingRight = '';
        document.documentElement.style.overflow = '';

        // Remove classes que podem estar afetando o pointer-events
        const bodyClasses = document.body.className.split(' ');
        const filteredClasses = bodyClasses.filter(cls => 
          !cls.includes('modal') && !cls.includes('dialog') && !cls.includes('overlay')
        );
        document.body.className = filteredClasses.join(' ');

        console.log('üßπ Limpeza agressiva de modais executada');
      };

      // Executa imediatamente
      immediateCleanup();

      // Executa a limpeza mais robusta ap√≥s um delay
      const delayedCleanup = setTimeout(() => {
        immediateCleanup();
        forceRemoveAllOverlays();
      }, 100);

      return () => clearTimeout(delayedCleanup);
    }
  }, [isOpen]);
}

